	(4, 1, '2015-03-03 07:51:21', '2015-03-03 07:51:21', '<section class="intro-image" style="background-image: url(&#39;<?php echo get_template_directory_uri(); ?>/images/cki-template11-headers-home.jpg&#39;);">\r\n    <header>\r\n        <h1>Understanding Wellbeing and Quality of Life</h1>\r\n        <hr>\r\n    </header>\r\n</section>\r\n<div class="section-with-slide">\r\n    <div class="row">\r\n        <section class="learnmore">\r\n            <h5><strong>Quality of Life Measures</strong></h5>\r\n            <br>\r\n            <p>We hope to inspire further public conversation and advance academic research around goals common to all of us – A better society through improvement in quality of life dimensions, such as peace, opportunity, standard of living, freedom, health, community and relationships.</p>\r\n            <a class="btn flat green" id="btn_learn_more" href="<?php echo home_url(); ?>/about">Learn More</a>\r\n        </section>\r\n        <section class="carousel single wide" id="maps">\r\n            <div class="row">\r\n                <ul class="slide-list">\r\n                    <li class="slide border">\r\n                        <header class="title-group">\r\n                            <h2>How Do You Compare?</h2>\r\n                            <p><p>See how your Quality of Life Measures compares to the rest of the Nation and the World.</p>\r\n                            </p>\r\n                        </header>\r\n                        <div class="image-block">\r\n                            <img src="<?php echo get_template_directory_uri(); ?>/images/cki-template1-home-carousel-1.jpg" alt="How Do You Compare?" />\r\n                        </div>\r\n                        <a class="btn flat blue " href="<?php echo home_url(); ?>/interactive-map" >Explore the Map</a>\r\n                    </li>\r\n                    <li class="slide border">\r\n                        <header class="title-group">\r\n                            <h2>Explore Quality of Life on A Global Scale</h2>\r\n                            <p><p>See how Your country or state quality of life compares to the Rest of the World</p>\r\n                                </p>\r\n                        </header>\r\n                        <div class="image-block">\r\n                            <img src="<?php echo get_template_directory_uri(); ?>/images/cki-template1-home-carousel-2.jpg" alt="Explore WellBeing on a Global Scale" />\r\n                        </div>\r\n                        <a class="btn flat blue " href="<?php echo home_url(); ?>/interactive-map" >Explore the Map</a>\r\n                    </li>\r\n                    <li class="slide border">\r\n                        <header class="title-group">\r\n                            <h2>Generate a Customized Quality of Life Measures Report</h2>\r\n                            <p><p>See detailed rankings across years, dimensions, or the components of the Quality of Life Measures.</p>\r\n                            </p>\r\n                        </header>\r\n                        <div class="image-block">\r\n                            <img src="<?php echo get_template_directory_uri(); ?>/images/cki-template1-home-carousel-3.jpg" alt="Generate a Customized WellBeing Report" />\r\n                        </div>\r\n                    </li>\r\n                </ul>\r\n            </div>\r\n        </section>\r\n    </div>\r\n</div>\r\n<a name="dimensions"></a>\r\n<section class="carousel dimensions carousel-home-page">\r\n    <div class="row">\r\n        <header class="title-group">\r\n            <h2 id="six_dimensions">Five Foundations of Quality of Life</h2>\r\n            \r\n        </header>\r\n        <ul class="slide-list">\r\n            <li class="slide us">\r\n                <p id="six_dimensions_description">Explore some of the important factors that increase happiness and life satisfaction in the U.S. states.</p>\r\n                <div class="dimension large-4-half medium-6 small-12 columns-no-padding community_and_relationships">\r\n                    <div class="title">\r\n                        <div class="image-container">\r\n                            <img id="symbol-rounded" src="<?php echo get_template_directory_uri(); ?>/images/dimensions_symbol/Symbol-CommunityRelationships.png">\r\n                        </div>\r\n                        <h3>Community &#038; Relationships</h3>\r\n                    </div>\r\n                     <div class="dimension-components">\r\n                     	<h4>Civic Commitment</h4>\r\n                     	<h4>Religious Engagement</h4>\r\n                    	<h4>Family Life</h4>\r\n                        <h4>Social life</h4>\r\n                    </div>\r\n                </div>\r\n                <div class="dimension large-4-half medium-6 small-12 columns-no-padding opportunity">\r\n                    <div class="title">\r\n                        <div class="image-container">\r\n                            <img id="symbol-rounded" src="<?php echo get_template_directory_uri(); ?>/images/dimensions_symbol/Symbol_Opportunity.png">\r\n                        </div>\r\n                        <h3>Freedom &#038; <br>Opportunity</h3>\r\n                    </div>\r\n                    <div class="dimension-components">\r\n                        <h4>Personal Freedom</h4>\r\n                        <h4>Economic Freedom</h4>\r\n                        <h4>Employment</h4>\r\n                        <h4>Perceptions of Opportunities</h4>\r\n                    </div>\r\n                </div>\r\n                <div class="dimension large-4-half medium-6 small-12 columns-no-padding health_and_environment">\r\n                    <div class="title">\r\n                        <div class="image-container">\r\n                            <img id="symbol-rounded" src="<?php echo get_template_directory_uri(); ?>/images/dimensions_symbol/Symbol-HealthEnvironment.png">\r\n                        </div>\r\n                        <h3>Health &#038; Environment</h3>\r\n                    </div>\r\n                     <div class="dimension-components">\r\n                        <h4>Physical Health</h4>\r\n                        <h4>Mental Health</h4>\r\n                        <h4>Environmental Health</h4>\r\n                    </div>\r\n                </div>\r\n                <!-- <div class="dimension large-4 medium-6 small-12 columns-no-padding freedom">\r\n                    <div class="title">\r\n                        <div class="image-container">\r\n                            <img id="symbol-rounded" src="<?php echo get_template_directory_uri(); ?>/images/dimensions_symbol/Symbol-Freedom.png">\r\n                        </div>\r\n                        <h3>Freedom</h3>\r\n                    </div>\r\n                     <div class="dimension-components">\r\n                        <h4>Personal Freedom</h4>\r\n                        <h4>Economic Freedom</h4>\r\n                    </div>\r\n                </div> -->\r\n                <div class="dimension large-4-half medium-6 small-12 columns-no-padding living_standard">\r\n                    <div class="title">\r\n                        <div class="image-container">\r\n                            <img id="symbol-rounded" src="<?php echo get_template_directory_uri(); ?>/images/dimensions_symbol/Symbol-LivingStandard.png">\r\n                        </div>\r\n                        <h3>Living Standard</h3>\r\n                    </div>\r\n                     <div class="dimension-components">\r\n                        <h4>Income</h4>\r\n                        <h4>Poverty</h4>\r\n                        <h4>Perceptions</h4>\r\n                    </div>\r\n                </div>\r\n                <div class="dimension large-4-half medium-6 small-12 columns-no-padding peace_and_security">\r\n                    <div class="title">\r\n                        <div class="image-container">\r\n                            <img id="symbol-rounded" src="<?php echo get_template_directory_uri(); ?>/images/dimensions_symbol/Symbol-PeaceSecurity.png">\r\n                        </div>\r\n                        <h3>Peace &#038; Security</h3>\r\n                    </div>\r\n                     <div class="dimension-components">\r\n                        <h4>Violent Crime</h4>\r\n                        <h4>Theft</h4>\r\n                        <h4>Trust</h4>\r\n                        <h4>Safety Perceptions</h4>\r\n                    </div>\r\n                </div>\r\n            </li>\r\n            <li class="slide world">\r\n                <p id="six_dimensions_description">Explore some of the important factors that increase happiness and life satisfaction Globally.</p>\r\n                <div class="dimension large-4-half medium-6 small-12 columns-no-padding community_and_relationships">\r\n                    <div class="title">\r\n                        <div class="image-container">\r\n                            <img id="symbol-rounded" src="<?php echo get_template_directory_uri(); ?>/images/dimensions_symbol/Symbol-CommunityRelationships.png">\r\n                        </div>\r\n                        <h3>Community &#038; Relationships</h3>\r\n                    </div>\r\n                     <div class="dimension-components">\r\n                        <h4>Civic Commitment</h4>\r\n                        <h4>Religious Engagement</h4>\r\n                        <h4>Family Life</h4>\r\n                        <h4>Social Life</h4>\r\n                    </div>\r\n                </div>\r\n                <div class="dimension large-4-half medium-6 small-12 columns-no-padding opportunity">\r\n                    <div class="title">\r\n                        <div class="image-container">\r\n                            <img id="symbol-rounded" src="<?php echo get_template_directory_uri(); ?>/images/dimensions_symbol/Symbol_Opportunity.png">\r\n                        </div>\r\n                        <h3>Freedom &#038; <br>Opportunity</h3>\r\n                    </div>\r\n                     <div class="dimension-components">\r\n                        <h4>Political Freedom</h4>\r\n                        <h4>Civil Liberties</h4>\r\n                        <h4>Religious Freedom</h4>\r\n                        <h4>Economic Freedom</h4>\r\n                        <h4>Perceived Opportunities</h4>\r\n                        <h4>Entrepreneurship</h4>\r\n                        <h4>Employment</h4>\r\n                    </div>\r\n                </div>\r\n                <div class="dimension large-4-half medium-6 small-12 columns-no-padding health_and_environment">\r\n                    <div class="title">\r\n                        <div class="image-container">\r\n                            <img id="symbol-rounded" src="<?php echo get_template_directory_uri(); ?>/images/dimensions_symbol/Symbol-HealthEnvironment.png">\r\n                        </div>\r\n                        <h3>Health &#038; Environment</h3>\r\n                    </div>\r\n                     <div class="dimension-components">\r\n                        <h4>Physical Health</h4>\r\n                        <h4>Mental Health</h4>\r\n                        <h4>Environmental Health</h4>\r\n                    </div>\r\n                </div>\r\n                <!-- <div class="dimension large-4 medium-6 small-12 columns-no-padding freedom">\r\n                    <div class="title">\r\n                        <div class="image-container">\r\n                            <img id="symbol-rounded" src="<?php echo get_template_directory_uri(); ?>/images/dimensions_symbol/Symbol-Freedom.png">\r\n                        </div>\r\n                        <h3>Freedom</h3>\r\n                    </div>\r\n                     <div class="dimension-components">\r\n                        <h4>Political Freedom</h4>\r\n                        <h4>Civil Liberties</h4>\r\n                        <h4>Religious Freedom</h4>\r\n                        <h4>Economic Freedom</h4>\r\n                        <h4>Perceived Freedom</h4>\r\n                    </div>\r\n                </div> -->\r\n                <div class="dimension large-4-half medium-6 small-12 columns-no-padding living_standard">\r\n                    <div class="title">\r\n                        <div class="image-container">\r\n                            <img id="symbol-rounded" src="<?php echo get_template_directory_uri(); ?>/images/dimensions_symbol/Symbol-LivingStandard.png">\r\n                        </div>\r\n                        <h3>Living Standard</h3>\r\n                    </div>\r\n                      <div class="dimension-components">\r\n                        <h4>Income</h4>\r\n                        <h4>Consumption</h4>\r\n                        <h4>Poverty</h4>\r\n                        <h4>Access to Resources</h4>\r\n                    </div>\r\n                </div>\r\n                <div class="dimension large-4-half medium-6 small-12 columns-no-padding peace_and_security">\r\n                    <div class="title">\r\n                        <div class="image-container">\r\n                            <img id="symbol-rounded" src="<?php echo get_template_directory_uri(); ?>/images/dimensions_symbol/Symbol-PeaceSecurity.png">\r\n                        </div>\r\n                        <h3>Peace &#038; Security</h3>\r\n                    </div>\r\n                    <div class="dimension-components">\r\n                        <h4>Violence and Warfare</h4>\r\n                        <h4>Human Rights</h4>\r\n                        <h4>Safety Perceptions</h4>\r\n                        <h4>Trust in Institutions</h4>\r\n                        <h4>Corruption</h4>\r\n                        <h4>Bureaucratic Quality</h4>\r\n                    </div>\r\n                </div>\r\n            </li>\r\n        </ul>\r\n    </div>\r\n</section>', 'Quality of Life', '', 'publish', 'open', 'closed', '', 'quality-of-life', '', '', '2016-03-10 18:51:17', '2016-03-10 18:51:17', '', 0, 'http://localhost/life-measures-wordpress/?page_id=4', 0, 'page', '', 0),
	(93, 1, '2015-03-03 17:07:26', '2015-03-03 17:07:26', '<section class="dimensions"><header class="title-group">\n<h2>The Six Dimensions of Quality of Life Measures</h2>\n\n<hr />\n\nExplore some of the important factors that increase happiness and life satisfaction.\n\n</header>\n<div class="row">\n<div class="vertical-align">\n<h3>Opportunity</h3>\n</div>\n<div class="vertical-align">\n<h3>Health &amp; Environment</h3>\n</div>\n<div class="vertical-align">\n<h3>Freedom</h3>\n</div>\n<div class="vertical-align">\n<h3>Community &amp; Relationships</h3>\n</div>\n<div class="vertical-align">\n<h3>Living Standard</h3>\n</div>\n<div class="vertical-align">\n<h3>Peace &amp; Security</h3>\n</div>\n</div>\n<a class="btn flat gray" href="&lt;?php echo TPATH ?&gt;quality_of_life_measures.php">Learn More</a>\n\n</section>', 'post', '', 'trash', 'closed', 'closed', '', 'post', '', '', '2015-03-03 17:20:27', '2015-03-03 17:20:27', '', 0, 'http://localhost/life-measures-wordpress/?post_type=html5-blank&#038;p=93', 0, 'html5-blank', '', 0),
	(197, 1, '2015-03-05 03:03:13', '2015-03-05 03:03:13', '<section class="intro-image" style="background-image: url(&#39;<?php echo get_template_directory_uri(); ?>/images/cki-template11-headers-faq.jpg&#39;);">\r\n    <header>\r\n        <h1>About the Quality of Life Measures</h1>\r\n        <hr>\r\n    </header>\r\n	</section>\r\n<div class="row">\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                <h2>About the Project</h2>\r\n                <hr>\r\n            </header>\r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                <p>What is quality of life?</p>\r\n                <p>Throughout human history, philosophers, poets, theologians, and scholars have sought the answer to that seemingly simple question. From King Solomon to the city squares of ancient Greece to worldwide internet debates, humanity has discussed just what ‘the good life’ might mean–and how to help more people achieve that.</p>\r\n                <a class="btn flat gray " href="<?php echo home_url(); ?>/the-concepts/#about_the_project">Learn More</a>\r\n            </article>\r\n        </div>\r\n    </section>\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                <h2>Advisory Board</h2>\r\n                <hr>\r\n            </header>\r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                <p>The Baugh Center’s advisory board is comprised of a diverse group of scholars, entrepreneurs, thought leaders and involved citizens who bring a variety of perspectives to the table. Their feedback is critical to fostering an informed and thoughtful dialogue on quality of life. Advisory Board members provide guidance for continuous improvement of our project.</p>\r\n                <a class="btn flat gray " href="<?php echo home_url(); ?>/contributors">Learn More</a>\r\n            </article>\r\n        </div>\r\n    </section>\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                <h2>Our Contributors</h2>\r\n                <hr>\r\n                </header>\r\n                <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                <p>The Quality of Life Measures Advisory Board is comprised of a diverse group of esteemed scholars, business leaders, and thought leaders who bring a variety of perspectives to the project. These experts and their feedback are critical to efforts to foster an informed and thoughtful dialogue on project. Advisory Board members provide strategic guidance for continuous improvement in the quality and value of the project’s research.</p>\r\n                <a class="btn flat gray jump" href="<?php echo home_url(); ?>/contributors">Meet our Contributors</a>\r\n            </article>\r\n        </div>\r\n    </section>\r\n</div>', 'About', '', 'publish', 'open', 'open', '', 'about', '', '', '2016-03-09 18:52:17', '2016-03-09 18:52:17', '', 0, 'http://localhost/life-measures-wordpress/?page_id=197', 0, 'page', '', 0),
	(199, 1, '2015-03-05 03:03:26', '2015-03-05 03:03:26', '<section class="intro-image" style="background-image: url(&#39;<?php echo get_template_directory_uri(); ?>/images/cki-template11-headers-aboutinit.jpg&#39;);">\r\n    <header>\r\n        <h1>Quality of Life Measures?</h1>\r\n        <hr>\r\n    </header>\r\n</section>\r\n<div class="row">\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                <h2>The Concepts of WellBeing and Quality of Life</h2>\r\n                <hr>\r\n                <p style="margin-top:200px;"><span style="color:green; font-style:italic;">“Quality of life can be examined by looking at overall subjective wellbeing—the sum of happiness and life satisfaction. The former represents pleasant emotions and feelings of joy  and the later refers to a person\'s reflection of his or her life as a whole.”</span></p>\r\n            </header>\r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                <p>Though the concepts have gone by other names and held different definitions, quality of life and human wellbeing have been a leading focus of scholarly, philosophical, and religious thought for much of human history. The great philosophers believed that wellbeing meant virtuous living. With the Enlightenment came the notion that absolute happiness could be achieved through human intervention in society’s established structures—that it was possible, in effect, to create a heaven on earth, as shown by the French revolutions and the various social experiments that followed.</p>\r\n                <p>With the rise of this notion, societal goals that had previously belonged to the informal realm of society—economic development; advancement of human capabilities, freedom and creativity; environmental sustainability; fairness; and social justice—now became core governmental objectives. Although the items mentioned above are important aspects of a good society, people of different philosophical, religious, cultural, and political opinions disagree as to their relative importance for quality of life.</p>\r\n                <p>The Baugh Center at Baylor University is launching this new, empirically based project because accurate measurements are indispensable for making decisions about how best to create a society with a high quality of life. When defined carefully, quality of life and wellbeing are holistic concepts that can be measured in a relatively precise manner and in objective, cross-cultural terms.</p>\r\n                <p>Together with many professionals in the wellbeing field, The Baugh Center considers wellbeing as a combination of two underlying components. The first is the more short-term and pleasant emotions and feelings of joy that a person experiences at any given time, and the second is a person’s cognitive, evaluative reflection of his or her life as a whole. The former is more closely aligned with what people today refer to as happiness and the latter is what scholars refer to as life satisfaction.</p>\r\n            </article>\r\n        </div>\r\n    </section>\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                <h2 id="about_the_project">About the Project</h2>\r\n                <hr>\r\n            </header>\r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                <p>What is quality of life?</p>\r\n                <p>Throughout human history, philosophers, poets, theologians, and scholars have sought the answer to that seemingly simple question. From King Solomon to the city squares of ancient Greece to worldwide internet debates, humanity has discussed just what ‘the good life’ might mean–and how to help more people achieve that.</p>\r\n                <p>The Baugh Center seeks to further this conversation as it continues into the 21st century. Through research, education, and discussion, we aim to advance understanding of how individual quality of life intersects with society as a whole;how to comprehend and measure the various aspects of wellbeing and quality of life;and how to inspire people to improve lives in their communities and society at large.</p>\r\n                <p>As part of this goal, we have produced measures of quality of life for all 50 U.S. states and more than 140 countries. These measures open new doors for research and form the core of our efforts to improve quality of life. The measures focus on six societal dimensions of human wellbeing: Community & Relationships, Health & Environment, Freedom, Living Standard, Opportunity, and Peace & Security.</p>\r\n            </article>\r\n\r\n        </div>\r\n    </section>\r\n</div>', 'The Concepts', '', 'publish', 'open', 'open', '', 'the-concepts', '', '', '2016-03-09 19:17:48', '2016-03-09 19:17:48', '', 0, 'http://localhost/life-measures-wordpress/?page_id=199', 0, 'page', '', 0),
	(201, 1, '2015-03-05 03:03:57', '2015-03-05 03:03:57', '<section class="intro-image" style="background-image: url(&#39;<?php echo get_template_directory_uri(); ?>/images/cki-template11-headers-advisory.jpg&#39;);">\r\n    <header>\r\n        <h1>Introducing our Contributors</h1>\r\n        <hr>\r\n    </header>\r\n</section>\r\n<div id="content" class="row">\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                <h2>Advisory Board</h2>\r\n                <hr>\r\n            </header>\r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                <p>The Baugh Center’s advisory board is comprised of a diverse group of scholars, entrepreneurs, thought leaders and involved citizens who bring a variety of perspectives to the table. Their feedback is critical to fostering an informed and thoughtful dialogue on quality of life. Advisory Board members provide guidance for continuous improvement of our project.</p>\r\n                <p>The role of the Advisory Board is to further the study and measurement of wellbeing and provide insight based on current discussions in economics, sociology, psychology, and other disciplines. All Advisory Board members review the Quality of Life Measures, offering feedback to further improve the methodology, data sets, and analysis.</p>\r\n                <p>In the spirit of the dialogue that accompanies this ever-developing field of work, each member of the board may not endorse every data interpretation or methodological choice. Such challenges serve to support the essence of the initiative by fostering rich and transparent discussion on the subjects examined.</p>\r\n            </article>\r\n        </div>\r\n    </section>\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                <h2>Research Contributors</h2>\r\n                <hr>\r\n            </header>\r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                <p>Content Here.</p>\r\n            </article>\r\n        </div>\r\n    </section>\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                \r\n            </header>\r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                \r\n            </article>\r\n        </div>\r\n    </section>\r\n    \r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                \r\n            </header>\r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                \r\n            </article>\r\n        </div>\r\n    </section>\r\n</div>     ', 'Contributors', '', 'publish', 'open', 'open', '', 'contributors', '', '', '2016-03-09 19:03:18', '2016-03-09 19:03:18', '', 0, 'http://localhost/life-measures-wordpress/?page_id=201', 0, 'page', '', 0),
	(203, 1, '2015-03-05 03:04:19', '2015-03-05 03:04:19', '<section class="intro-image" style="background-image: url(&#39;<?php echo get_template_directory_uri(); ?>/images/cki-template11-headers-api.jpg&#39;);"> \r\n	<header>\r\n	    <h1>How to Use Our Data API</h1>\r\n	    <hr>\r\n	</header>\r\n</section>\r\n<div class="row">\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                <h2>Introduction</h2>\r\n                <hr>\r\n            </header>\r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                <p>Before you can access the API data, you will first need to get a registration key that you will need to use on all of your requests. <a href="<?php echo home_url(); ?>/api-registration">Click here</a> to get your registration key.</p>\r\n                <p>To gain access to raw information regarding the locations, dimensions, components, and scores affiliated with each Measure (U.S. Measurements or World Measurements), please use one of the following URL use cases.</p>\r\n            </article>\r\n        </div>\r\n    </section>\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                <h2>Resources</h2>\r\n                <hr>\r\n            </header>\r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                <table>\r\n                    <tbody>\r\n                        <tr>\r\n                            <th>Resources</th>\r\n                            <th>Description</th>\r\n                        </tr>\r\n                        <tr>\r\n                            <td>\r\n                                <h4>U.S.</h4>\r\n                                <p>http://life-measures-wordpress.connxt.net/api/index.php/us/locations/?key=yourRegistrationKey</p>\r\n                                <h4>World</h4>\r\n                                <p>http://life-measures-wordpress.connxt.net/api/index.php/int/locations/?key=yourRegistrationKey</p>\r\n                            </td>\r\n                            <td>\r\n                                <strong>Locations</strong><br>\r\n                                    Returns a list of all states or nations included in the Quality of Life Measures.\r\n                            </td>\r\n                        </tr>\r\n                        <tr>\r\n                            <td>\r\n                                <h4>U.S.</h4>\r\n                                <p>http://life-measures-wordpress.connxt.net/api/index.php/us/dimensionlist/?key=yourRegistrationKey</p>\r\n                                <h4>World</h4>\r\n                                <p>http://life-measures-wordpress.connxt.net/api/index.php/int/dimensionlist/?key=yourRegistrationKey</p>\r\n                            </td>\r\n                            <td>\r\n                                <strong>Dimensions</strong><br>\r\n                                Returns a list of the dimensions of quality of life measure, which are shared by the U.S. and World Measurements.\r\n                            </td>\r\n                        </tr>\r\n                        <tr>\r\n                            <td>\r\n                                <h4>U.S.</h4>\r\n                                <p>http://life-measures-wordpress.connxt.net/api/index.php/us/componentlist/?key=yourRegistrationKey</p>\r\n                                <h4>World</h4>\r\n                                <p>http://life-measures-wordpress.connxt.net/api/index.php/int/componentlist/?key=yourRegistrationKey</p>\r\n                            </td>\r\n                            <td>\r\n                                <strong>Components</strong><br>\r\n                                Returns a list of all components (and their parent dimensions) included in the U.S. and World Measurements.\r\n                            </td>\r\n                        </tr>\r\n                        <tr>\r\n                            <td>\r\n                                <h4>U.S.</h4>\r\n                                <p>http://life-measures-wordpress.connxt.net/api/index.php/us/subcomponentlist/?key=yourRegistrationKey</p>\r\n                                <h4>World</h4>\r\n                                <p>http://life-measures-wordpress.connxt.net/api/index.php/int/subcomponentlist/?key=yourRegistrationKey</p>\r\n                            </td>\r\n                            <td>\r\n                                <strong>Sub-Components</strong><br>\r\n                                Returns a list of all sub-components (and their parent components) included in the U.S. and World Measurements.\r\n                            </td>\r\n                        </tr>\r\n                        <tr>\r\n                            <td>\r\n                                <h4>U.S.</h4>\r\n                                <p>http://life-measures-wordpress.connxt.net/api/index.php/us/subsubcomponentlist/?key=yourRegistrationKey</p>\r\n                                <h4>World</h4>\r\n                                <p>http://life-measures-wordpress.connxt.net/api/index.php/int/subsubcomponentlist/?key=yourRegistrationKey</p>\r\n                            </td>\r\n                            <td>\r\n                                <strong>Sub-Sub-Components</strong>\r\n                                <br>\r\n                                Returns a list of all sub-sub-components (and their parent sub-components) included in the U.S. and World Measurements.\r\n                            </td>\r\n                        </tr>\r\n                    </tbody>\r\n                </table>\r\n            </article>\r\n        </div>\r\n    </section>\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                <h2>Scores</h2>\r\n                <hr>\r\n            </header>\r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                <table>\r\n                    <tbody>\r\n                        <tr>\r\n                            <th>Scores</th>\r\n                            <th>Description</th>\r\n                        </tr>\r\n                        <tr>\r\n                            <td>\r\n                            <h4>U.S.</h4>\r\n                            <p>http://life-measures-wordpress.connxt.net/api/index.php/us/summary/?key=yourRegistrationKey</p>\r\n                            <h4>World</h4>\r\n                            <p>http://life-measures-wordpress.connxt.net/api/index.php/int/summary/?key=yourRegistrationKey</p></td>\r\n                            <td>\r\n                                <strong>Summary Scores</strong><br>\r\n                                Returns all states or nations, scores and rankings related with the Quality of Life Measures for the most recent year that we have data.</td>\r\n                        </tr>\r\n                        <tr>\r\n                            <td>\r\n                                <h4>U.S.</h4>\r\n                                <p>http://life-measures-wordpress.connxt.net/api/index.php/us/scores/?key=yourRegistrationKey</p>\r\n                                <h4>World</h4>\r\n                                <p>http://life-measures-wordpress.connxt.net/api/index.php/int/scores/?key=yourRegistrationKey</p>\r\n                            </td>\r\n                            <td>\r\n                                <strong>All Scores</strong><br>\r\n                                    Returns all states or nations, dimensions, components, subcomponents, sub-sub-components, scores and rankings related with the Quality of Life Measures. Includes historical data back to 2001 for US data, and 2000 for World data.\r\n                            </td>\r\n                        </tr>\r\n                        <tr>\r\n                            <td>\r\n                                <h4>U.S.</h4>\r\n                                <p>http://life-measures-wordpress.connxt.net/api/index.php/us/dimensions/?key=yourRegistrationKey</p>\r\n                                <h4>World</h4>\r\n                                <p>http://life-measures-wordpress.connxt.net/api/index.php/int/dimensions/?key=yourRegistrationKey</p>\r\n                            </td>\r\n                            <td>\r\n                                <strong>Dimension Scores</strong>\r\n                                <br>\r\n                                Returns all dimension scores from all states or nations (and from all years) included in the Measure.</td>\r\n                        </tr>\r\n                        <tr>\r\n                            <td>\r\n                                <h4>U.S.</h4>\r\n                                <p>http://life-measures-wordpress.connxt.net/api/index.php/us/components/?key=yourRegistrationKey</p>\r\n                                <h4>World</h4>\r\n                                <p>http://life-measures-wordpress.connxt.net/api/index.php/int/components/?key=yourRegistrationKey</p>\r\n                            </td>\r\n                            <td>\r\n                                <strong>Component Scores</strong>\r\n                                Returns all component scores from all states or nations (and from all years) included in the Measure.\r\n                            </td>\r\n                        </tr>\r\n                        <tr>\r\n                            <td>\r\n                                <h4>U.S.</h4>\r\n                                <p>http://life-measures-wordpress.connxt.net/api/index.php/us/subcomponents/?key=yourRegistrationKey</p>\r\n                                <h4>World</h4>\r\n                                <p>http://life-measures-wordpress.connxt.net/api/index.php/int/subcomponents/?key=yourRegistrationKey</p>\r\n                            </td>\r\n                            <td>\r\n                                <strong>Sub-Component Scores</strong><br>\r\n                                Returns all sub-component scores from all states or nations (and from all years) included in the Measure.\r\n                            </td>\r\n                        </tr>\r\n                        <tr>\r\n                            <td>\r\n                                <h4>U.S.</h4>\r\n                                <p>http://life-measures-wordpress.connxt.net/api/index.php/us/subsubcomponents/?key=yourRegistrationKey</p>\r\n                                <h4>World</h4>\r\n                                <p>http://life-measures-wordpress.connxt.net/api/index.php/int/subsubcomponents/?key=yourRegistrationKey</p>\r\n                            </td>\r\n                            <td><strong>Sub-Sub-Component Scores</strong><br>\r\n                                Returns all sub-sub-component scores from all states or nations (and from all years) included in the Measure.\r\n                            </td>\r\n                        </tr>\r\n                    </tbody>\r\n                </table>\r\n            </article>\r\n        </div>\r\n    </section>\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                <h2>Targeted Queries</h2>\r\n                <hr>\r\n            </header>\r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                <p>Generating targeted lists of specific data points is done by adding query parameters to your URL. This can allow you to specify your search criteria (ex.; locations, years, dimensions, components, etc.) and get a detailed object of that data as well as a calculated score and ranking based on the returned data.</p>\r\n                <h3>Base URLs</h3>\r\n                <table>\r\n                    <tbody>\r\n                        <tr>\r\n                            <td>U.S.</td>\r\n                            <td>\r\n                                <p>http://life-measures-wordpress.connxt.net/api/index.php/us/scores/?key=yourRegistrationKey</p>\r\n                                <p>This call will return all states, dimensions, components, subcomponents, sub-sub-components, and all of the scores related to the Quality of Life U.S. Measurements. Includes historical data back to 2001.\r\n                                </p>\r\n                            </td>\r\n                        </tr>\r\n                        <tr>\r\n                            <td>World</td>\r\n                            <td>\r\n                                <p>http://life-measures-wordpress.connxt.net/api/index.php/int/scores/?key=yourRegistrationKey</p>\r\n                                <p>This call will return all countries, dimensions, components, subcomponents, sub-sub-components, and all of the scores related to the Quality of Life World Measurements. Includes historical data back to 2000.\r\n                                </p>\r\n                            </td>\r\n                        </tr>\r\n                    </tbody>\r\n                </table>\r\n                <h3>Parameters</h3>\r\n                <table>\r\n                    <tbody>\r\n                        <tr>\r\n                            <td>locations</td>\r\n                            <td>\r\n                                Returns all of the data for the individual states passed in on the request. Also compares the scores of the returned states and ranks them accordingly.<p></p>\r\n                                <p>States and countries are passed in as a pipe-delimited list of the two-letter state/country abbreviations.</p>\r\n                                <h4>Example</h4>\r\n                                <p>http://life-measures-wordpress.connxt.net/api/index.php/us/scores/?key=yourRegistrationKey&amp;locations=ca|ny|va</p>\r\n                                <div class="row">\r\n                                    <div class="large-6 medium-6 columns">\r\n                                        <div class="parameter-link">\r\n                                            <a href="#">Available U.S. Locations\r\n                                                 <i class="fi-arrow-down"></i>\r\n                                                 <i class="fi-arrow-up"></i>\r\n                                            </a>\r\n                                        </div>\r\n                                        <div class="parameter-list">\r\n                                            AL → Alabama<br>\r\n                                            AK → Alaska<br>\r\n                                            AZ → Arizona<br>\r\n                                            AR → Arkansas<br>\r\n                                            CA → California<br>\r\n                                            CO → Colorado<br>\r\n                                            CT → Connecticut<br>\r\n                                            DE → Delaware<br>\r\n                                            FL → Florida<br>\r\n                                            GA → Georgia<br>\r\n                                            HI → Hawaii<br>\r\n                                            ID → Idaho<br>\r\n                                            IL → Illinois<br>\r\n                                            IN → Indiana<br>\r\n                                            IA → Iowa<br>\r\n                                            KS → Kansas<br>\r\n                                            KY → Kentucky<br>\r\n                                            LA → Louisiana<br>\r\n                                            ME → Maine<br>\r\n                                            MD → Maryland<br>\r\n                                            MA → Massachusetts<br>\r\n                                            MI → Michigan<br>\r\n                                            MN → Minnesota<br>\r\n                                            MS → Mississippi<br>\r\n                                            MO → Missouri<br>\r\n                                            MT → Montana<br>\r\n                                            NE → Nebraska<br>\r\n                                            NV → Nevada<br>\r\n                                            NH → New Hampshire<br>\r\n                                            NJ → New Jersey<br>\r\n                                            NM → New Mexico<br>\r\n                                            NY → New York<br>\r\n                                            NC → North Carolina<br>\r\n                                            ND → North Dakota<br>\r\n                                            OH → Ohio<br>\r\n                                            OK → Oklahoma<br>\r\n                                            OR → Oregon<br>\r\n                                            PA → Pennsylvania<br>\r\n                                            RI → Rhode Island<br>\r\n                                            SC → South Carolina<br>\r\n                                            SD → South Dakota<br>\r\n                                            TN → Tennessee<br>\r\n                                            TX → Texas<br>\r\n                                            UT → Utah<br>\r\n                                            VT → Vermont<br>\r\n                                            VA → Virginia<br>\r\n                                            WA → Washington<br>\r\n                                            WV → West Virginia<br>\r\n                                            WI → Wisconsin<br>\r\n                                            WY → Wyoming\r\n                                        </div>\r\n                                        <p></p>\r\n                                    </div>\r\n                                    <div class="large-6 medium-6 columns">\r\n                                        <div class="parameter-link">\r\n                                            <a href="#">Available World Locations \r\n                                                <i class="fi-arrow-down"></i>\r\n                                                <i class="fi-arrow-up"></i>\r\n                                            </a>\r\n                                        </div>\r\n                                        <div class="parameter-list">\r\n                                            AF → Afghanistan<br>\r\n                                            AL → Albania<br>\r\n                                            DZ → Algeria<br>\r\n                                            AO → Angola<br>\r\n                                            AR → Argentina<br>\r\n                                            AM → Armenia<br>\r\n                                            AU → Australia<br>\r\n                                            AT → Austria<br>\r\n                                            AZ → Azerbaijan<br>\r\n                                            BD → Bangladesh<br>\r\n                                            BY → Belarus<br>\r\n                                            BE → Belgium<br>\r\n                                            BZ → Belize<br>\r\n                                            BJ → Benin<br>\r\n                                            BO → Bolivia<br>\r\n                                            BA → Bosnia<br>\r\n                                            BW → Botswana<br>\r\n                                            BR → Brazil<br>\r\n                                            BG → Bulgaria<br>\r\n                                            BF → Burkina Faso<br>\r\n                                            BI → Burundi<br>\r\n                                            KH → Cambodia<br>\r\n                                            CM → Cameroon<br>\r\n                                            CA → Canada<br>\r\n                                            CF → Central African Rep<br>\r\n                                            TD → Chad<br>\r\n                                            CL → Chile<br>\r\n                                            CN → China<br>\r\n                                            CO → Colombia<br>\r\n                                            KM → Comoros<br>\r\n                                            CD → Congo, Dem. Rep.<br>\r\n                                            CG → Congo, Rep.<br>\r\n                                            CR → Costa Rica<br>\r\n                                            CI → Cote D Ivoire<br>\r\n                                            HR → Croatia<br>\r\n                                            CY → Cyprus<br>\r\n                                            CZ → Czech Republic<br>\r\n                                            DK → Denmark<br>\r\n                                            DJ → Djibouti<br>\r\n                                            DO → Dominican Republic<br>\r\n                                            EC → Ecuador<br>\r\n                                            EG → Egypt, Arab Rep.<br>\r\n                                            SV → El Salvador<br>\r\n                                            EE → Estonia<br>\r\n                                            ET → Ethiopia<br>\r\n                                            FI → Finland<br>\r\n                                            FR → France<br>\r\n                                            GE → Georgia<br>\r\n                                            DE → Germany<br>\r\n                                            GH → Ghana<br>\r\n                                            GR → Greece<br>\r\n                                            GT → Guatemala<br>\r\n                                            GN → Guinea<br>\r\n                                            GY → Guyana<br>\r\n                                            HT → Haiti<br>\r\n                                            HN → Honduras<br>\r\n                                            HU → Hungary<br>\r\n                                            IS → Iceland<br>\r\n                                            IN → India<br>\r\n                                            ID → Indonesia<br>\r\n                                            IR → Iran, Islamic Rep.<br>\r\n                                            IQ → Iraq<br>\r\n                                            IE → Ireland<br>\r\n                                            IL → Israel<br>\r\n                                            IT → Italy<br>\r\n                                            JM → Jamaica<br>\r\n                                            JP → Japan<br>\r\n                                            JO → Jordan<br>\r\n                                            KZ → Kazakhstan<br>\r\n                                            KE → Kenya<br>\r\n                                            KR → Korea, Rep.<br>\r\n                                            KW → Kuwait<br>\r\n                                            KG → Kyrgyz Republic<br>\r\n                                            LA → Lao Pdr<br>\r\n                                            LV → Latvia<br>\r\n                                            LB → Lebanon<br>\r\n                                            LR → Liberia<br>\r\n                                            LT → Lithuania<br>\r\n                                            LU → Luxembourg<br>\r\n                                            MK → Macedonia, Fyr<br>\r\n                                            MG → Madagascar<br>\r\n                                            MW → Malawi<br>\r\n                                            MY → Malaysia<br>\r\n                                            ML → Mali<br>\r\n                                            MT → Malta<br>\r\n                                            MR → Mauritania<br>\r\n                                            MX → Mexico<br>\r\n                                            MD → Moldova<br>\r\n                                            MN → Mongolia<br>\r\n                                            ME → Montenegro<br>\r\n                                            MA → Morocco<br>\r\n                                            MZ → Mozambique<br>\r\n                                            NA → Namibia<br>\r\n                                            NP → Nepal<br>\r\n                                            NL → Netherlands<br>\r\n                                            NZ → New Zealand<br>\r\n                                            NI → Nicaragua<br>\r\n                                            NE → Niger<br>\r\n                                            NG → Nigeria<br>\r\n                                            NO → Norway<br>\r\n                                            PK → Pakistan<br>\r\n                                            PA → Panama<br>\r\n                                            PY → Paraguay<br>\r\n                                            PE → Peru<br>\r\n                                            PH → Philippines<br>\r\n                                            PL → Poland<br>\r\n                                            PT → Portugal<br>\r\n                                            QA → Qatar<br>\r\n                                            RO → Romania<br>\r\n                                            RU → Russia<br>\r\n                                            RW → Rwanda<br>\r\n                                            SN → Senegal<br>\r\n                                            RS → Serbia<br>\r\n                                            SL → Sierra Leone<br>\r\n                                            SG → Singapore<br>\r\n                                            SK → Slovak Republic<br>\r\n                                            SI → Slovenia<br>\r\n                                            ZA → South Africa<br>\r\n                                            ES → Spain<br>\r\n                                            LK → Sri Lanka<br>\r\n                                            SD → Sudan<br>\r\n                                            SE → Sweden<br>\r\n                                            CH → Switzerland<br>\r\n                                            SY → Syrian<br>\r\n                                            TJ → Tajikistan<br>\r\n                                            TZ → Tanzania<br>\r\n                                            TH → Thailand<br>\r\n                                            TG → Togo<br>\r\n                                            TT → Trinidad And Tobago<br>\r\n                                            TN → Tunisia<br>\r\n                                            TR → Turkey<br>\r\n                                            UG → Uganda<br>\r\n                                            UA → Ukraine<br>\r\n                                            UK → United Kingdom<br>\r\n                                            US → United States<br>\r\n                                            UY → Uruguay<br>\r\n                                            UZ → Uzbekistan<br>\r\n                                            VE → Venezuela, Rb<br>\r\n                                            VN → Vietnam<br>\r\n                                            YE → Yemen, Rep.<br>\r\n                                            ZM → Zambia<br>\r\n                                            ZW → Zimbabwe\r\n                                        </div>\r\n                                        <p></p>\r\n                                    </div>\r\n                                    <p></p>\r\n                                </div>\r\n                            </td>\r\n                        </tr>\r\n                        <tr>\r\n                            <td>years</td>\r\n                            <td>\r\n                                Returns all of the data for the individual years passed in on the request. Also compares the state scores of the returned years and ranks those states accordingly.\r\n                                <p></p>\r\n                                <p>Years are passed in as a pipe-delimited list of the four-digit year.</p>\r\n                                <h4>Example</h4>\r\n                                <p>http://life-measures-wordpress.connxt.net/api/index.php/us/scores/?key=yourRegistrationKey&amp;years=2003|2004|2005</p>\r\n                                <div class="row">\r\n                                    <div class="large-6 medium-6 columns">\r\n                                        <div class="parameter-link">\r\n                                            <a href="#">Available U.S. Years\r\n                                                <i class="fi-arrow-down"></i>\r\n                                                <i class="fi-arrow-up"></i></a>\r\n                                        </div>\r\n                                        <div class="parameter-list">\r\n                                            2001<br>\r\n                                            2002<br>\r\n                                            2003<br>\r\n                                            2004<br>\r\n                                            2005<br>\r\n                                            2006<br>\r\n                                            2007<br>\r\n                                            2008<br>\r\n                                            2009<br>\r\n                                            2010<br>\r\n                                            2011<br>\r\n                                            2012\r\n                                        </div>\r\n                                        <p></p>\r\n                                    </div>\r\n                                    <div class="large-6 medium-6 columns">\r\n                                        <div class="parameter-link">\r\n                                            <a href="#">Available World Years \r\n                                            <i class="fi-arrow-down"></i>\r\n                                            <i class="fi-arrow-up"></i></a>\r\n                                        </div>\r\n                                        <div class="parameter-list">\r\n                                            2000<br>\r\n                                            2001<br>\r\n                                            2002<br>\r\n                                            2003<br>\r\n                                            2004<br>\r\n                                            2005<br>\r\n                                            2006<br>\r\n                                            2007<br>\r\n                                            2008<br>\r\n                                            2009<br>\r\n                                            2010<br>\r\n                                            2011<br>\r\n                                            2012\r\n                                        </div>\r\n                                        <p></p>\r\n                                    </div>\r\n                                    <p></p>\r\n                                </div>\r\n                            </td>\r\n                        </tr>\r\n                        <tr>\r\n                            <td>dimensions</td>\r\n                            <td>\r\n                                Returns the data for the individual dimensions passed in on the request. Also compares the scores of the returned dimensions and ranks the states accordingly.<p></p>\r\n                                <p>Dimensions are passed in as a pipe-delimited list of the dimension name.</p>\r\n                                <h4>Example</h4>\r\n                                <p>http://life-measures-wordpress.connxt.net/api/index.php/us/scores/?key=yourRegistrationKey&amp;dimensions=opportunity|health_and_environment|freedom</p>\r\n                                <div class="row">\r\n                                    <div class="large-12 medium-12 columns">\r\n                                        <div class="parameter-link">\r\n                                            <a href="#">Available U.S. &amp; World Dimensions\r\n                                                <i class="fi-arrow-down"></i>\r\n                                                <i class="fi-arrow-up"></i>\r\n                                            </a>\r\n                                        </div>\r\n                                        <div class="parameter-list">\r\n                                            opportunity<br>\r\n                                            health_and_environment<br>\r\n                                            freedom<br>\r\n                                            community_and_relationships<br>\r\n                                            living_standard<br>\r\n                                            peace_and_security\r\n                                        </div>\r\n                                        <p></p>\r\n                                    </div>\r\n                                    <p></p>\r\n                                </div>\r\n                            </td>\r\n                        </tr>\r\n                        <tr>\r\n                            <td>components</td>\r\n                            <td>\r\n                                Returns the data for the individual components passed in on the request. Also compares the scores of the returned components and ranks the states accordingly.<p></p>\r\n                                <p>Components are passed in as a pipe-delimited list of the components name.</p>\r\n                                <h4>Example</h4>\r\n                                <p>http://life-measures-wordpress.connxt.net/api/index.php/us/scores/?key=yourRegistrationKey&amp;components=poverty|employment|community_vitality</p>\r\n                                <div class="row">\r\n                                    <div class="large-12 medium-12 columns">\r\n                                        <div class="parameter-link">\r\n                                            <a href="#">Available U.S. Components \r\n                                                <i class="fi-arrow-down"></i>\r\n                                                <i class="fi-arrow-up"></i>\r\n                                            </a>\r\n                                        </div>\r\n                                        <div class="parameter-list">\r\n                                            <strong>opportunity:</strong><br>\r\n                                            poverty<br>\r\n                                            employment<br>\r\n                                            community_vitality<br>\r\n                                            education<br>\r\n                                            <strong>health_and_environment:</strong><br>\r\n                                            physical_health<br>\r\n                                            mental_health<br>\r\n                                            environmental_health<br>\r\n                                            <strong>freedom:</strong><br>\r\n                                            personal_freedom<br>\r\n                                            economic_freedom<br>\r\n                                            <strong>community_and_relationships:</strong><br>\r\n                                            community_life<br>\r\n                                            family_life<br>\r\n                                            <strong>living_standard:</strong><br>\r\n                                            current_living_standard<br>\r\n                                            improvement_in_living_standard<br>\r\n                                            <strong>peace_and_security:</strong><br>\r\n                                            violent_crime_rate<br>\r\n                                            property_crime_rate<br>\r\n                                            perceived_local_safety<br>\r\n                                            trust\r\n                                            </div>\r\n                                             <p></p>\r\n                                    </div>\r\n                                    <div class="large-12 medium-12 columns">\r\n                                        <div class="parameter-link">\r\n                                            <a href="#">Available World Components \r\n                                            <i class="fi-arrow-down"></i>\r\n                                            <i class="fi-arrow-up"></i></a>\r\n                                        </div>\r\n                                        <div class="parameter-list">\r\n                                            <strong>opportunity:</strong><br>\r\n                                            poverty<br>\r\n                                            employment<br>\r\n                                            community_basics<br>\r\n                                            entrepreneurship<br>\r\n                                            <strong>health_and_environment:</strong><br>\r\n                                            physical_health<br>\r\n                                            mental_health<br>\r\n                                            environmental_health<br>\r\n                                            <strong>freedom:</strong><br>\r\n                                            political_freedom<br>\r\n                                            civil_liberties<br>\r\n                                            religious_freedom<br>\r\n                                            economic_freedom<br>\r\n                                            perceived_freedom<br>\r\n                                            <strong>community_and_relationships:</strong><br>\r\n                                            community_life<br>\r\n                                            family_life<br>\r\n                                            <strong>living_standard:</strong><br>\r\n                                            current_living_standard<br>\r\n                                            improvement_in_living_standard<br>\r\n                                            <strong>peace_and_security:</strong><br>\r\n                                            violence_and_ethnic_warfare<br>\r\n                                            human_rights_violations<br>\r\n                                            law_and_order<br>\r\n                                            trust_in_national_institutions<br>\r\n                                            corruption_in_business_and_government<br>\r\n                                            bureaucracy_quality\r\n                                        </div>\r\n                                        <p></p>\r\n                                    </div>\r\n                                    <p></p>\r\n                                </div>\r\n                            </td>\r\n                        </tr>\r\n                         <tr>\r\n                            <td>sub-components</td>\r\n                            <td>\r\n                                Returns the data for the individual subcomponents passed in on the request. Also compares the scores of the returned subcomponents and ranks the states accordingly.<p></p>\r\n                                <p>Subcomponents are passed in as a pipe-delimited list of the subcomponents name.</p>\r\n                                <h4>Example</h4>\r\n                                <p>http://life-measures-wordpress.connxt.net/api/index.php/us/scores/?key=yourRegistrationKey&amp;subcomponents=poverty_rate|unemployment_rate|employment_growth</p>\r\n                                <div class="row">\r\n                                    <div class="large-12 medium-12 columns">\r\n                                        <div class="parameter-link">\r\n                                        <a href="#">Available U.S. Subcomponents \r\n                                            <i class="fi-arrow-down"></i>\r\n                                            <i class="fi-arrow-up"></i></a>\r\n                                        </div>\r\n                                        <div class="parameter-list">\r\n                                            <strong>poverty:</strong><br>\r\n                                            poverty_rate<br>\r\n                                            income_of_the_bottom_25<br>\r\n                                            <strong>employment:</strong><br>\r\n                                            unemployment_rate<br>\r\n                                            long_term_unemployment_rate<br>\r\n                                            labor_participation_rate<br>\r\n                                            <strong>community_vitality:</strong><br>\r\n                                            perceived_community_improvement<br>\r\n                                            employment_growth<br>\r\n                                            percent_increase_in_businesses<br>\r\n                                            <strong>education:</strong><br>\r\n                                            8th_grade_test_scores<br>\r\n                                            high_school_dropout_rate<br>\r\n                                            <strong>physical_health:</strong><br>\r\n                                            male_life_expectancy<br>\r\n                                            female_life_expectancy<br>\r\n                                            obesity_rate<br>\r\n                                            <strong>mental_health:</strong><br>\r\n                                            suicide_rate<br>\r\n                                            prevalence_of_worrying<br>\r\n                                            prevalence_of_anger<br>\r\n                                            <strong>environmental_health:</strong><br>\r\n                                            satisfaction_with_location<br>\r\n                                            access_to_safe_water<br>\r\n                                            access_to_fruits_and_vegetables<br>\r\n                                            access_to_medicine<br>\r\n                                            access_to_a_safe_place_to_exercise<br>\r\n                                            <strong>community_life:</strong><br>\r\n                                            volunteer_rate<br>\r\n                                            religious_engagement<br>\r\n                                            <strong>family_life:</strong><br>\r\n                                            prevalence_of_marriage<br>\r\n                                            prevalence_of_divorce<br>\r\n                                            children_in_single_parent_families<br>\r\n                                            time_spent_with_friends_and_family<br>\r\n                                            <strong>current_living_standard:</strong><br>\r\n                                            personal_income_per_capita<br>\r\n                                            perceived_current_standard_of_living<br>\r\n                                            <strong>improvement_in_living_standard:</strong><br>\r\n                                            personal_income_per_capita_growth<br>\r\n                                            perceived_improvement_in_living_standard<br>\r\n                                            <strong>education:</strong><br>\r\n                                            choice\r\n                                        </div>\r\n                                        <p></p>\r\n                                    </div>\r\n                                    <div class="large-12 medium-12 columns">\r\n                                        <div class="parameter-link">\r\n                                            <a href="#">Available World Subcomponents \r\n                                                <i class="fi-arrow-down"></i>\r\n                                                <i class="fi-arrow-up"></i></a>\r\n                                        </div>\r\n                                        <div class="parameter-list">\r\n                                            <strong>poverty:</strong><br>\r\n                                            poverty_rate<br>\r\n                                            income_of_bottom_20<br>\r\n                                            <strong>employment:</strong><br>\r\n                                            unemployment_rate<br>\r\n                                            labor_participation_rate<br>\r\n                                            perceived_job_availability<br>\r\n                                            <strong>community_vitality:</strong><br>\r\n                                            access_to_basic_needs<br>\r\n                                            communications_infrastructure<br>\r\n                                            <strong>entrepreneurship:</strong><br>\r\n                                            business_startup_costs<br>\r\n                                            time_to_start_a_business<br>\r\n                                            perceived_climate_for_new_businesses<br>\r\n                                            <strong>physical_health:</strong><br>\r\n                                            life_expectancy_at_birth<br>\r\n                                            life_expectancy_at_age_60<br>\r\n                                            infant_mortality_rate<br>\r\n                                            undernourishment<br>\r\n                                            <strong>mental_and_emotional_health:</strong><br>\r\n                                            suicide_rate<br>\r\n                                            positive_experience<br>\r\n                                            suffering<br>\r\n                                            <strong>environmental_health:</strong><br>\r\n                                            air_quality<br>\r\n                                            water_quality<br>\r\n                                            sanitation<br>\r\n                                            <strong>political_freedom:</strong><br>\r\n                                            political_rights<br>\r\n                                            expression_of_political_views_without_fear<br>\r\n                                            <strong>community_life:</strong><br>\r\n                                            civic_engagement<br>\r\n                                            religious_engagement<br>\r\n                                            <strong>family_life:</strong><br>\r\n                                            prevalence_of_marriage<br>\r\n                                            prevalence_of_divorce<br>\r\n                                            time_spent_with_friends_and_family<br>\r\n                                            <strong>current_living_standard:</strong><br>\r\n                                            gdp_per_capita<br>\r\n                                            perceived_current_living_standard<br>\r\n                                            <strong>improvement_in_living_standard:</strong><br>\r\n                                            gdp_per_capita_growth<br>\r\n                                            perceived_improvement_in_living_standard<br>\r\n                                            <strong>community_vitality:</strong><br>\r\n                                            literacy_rate<br>\r\n                                            <strong>family_life:</strong><br>\r\n                                            children_living_with_biological_parents\r\n                                        </div>\r\n                                        <p></p>\r\n                                    </div>\r\n                                    <p></p>\r\n                                </div>\r\n                            </td>\r\n                        </tr>\r\n                        <tr>\r\n                            <td>\r\n                            sub-sub-components\r\n                            </td>\r\n                            <td>\r\n                                Returns the data for the individual sub-subcomponents passed in on the request. Also compares the scores of the returned sub-sub-components and ranks the states accordingly.<p></p>\r\n                                <p>Sub-subcomponents are passed in as a pipe-delimited list of the sub-subcomponents name.</p>\r\n                                <h4>Example</h4>\r\n                                <p>http://life-measures-wordpress.connxt.net/api/index.php/us/scores/?key=yourRegistrationKey&amp;sub-sub-subcomponents=reading|math|science</p>\r\n                                <div class="row">\r\n                                    <div class="large-12 medium-12 columns">\r\n                                        <div class="parameter-link">\r\n                                            <a href="#">Available U.S. Sub-subcomponents \r\n                                            <i class="fi-arrow-down"></i>\r\n                                            <i class="fi-arrow-up"></i></a>\r\n                                        </div>\r\n                                        <div class="parameter-list">\r\n                                            <strong>8th_grade_test_scores:</strong><br>\r\n                                            reading<br>\r\n                                            math<br>\r\n                                            science<br>\r\n                                            <strong>high_school_dropout_rate:</strong><br>\r\n                                            prevalence_of_charter_schools<br>\r\n                                            educational_freedom<br>\r\n                                            <strong>religious_engagement:</strong><br>\r\n                                            importance_of_religion<br>\r\n                                            religious_attendance<br>\r\n                                            <strong>perceived_improvement_in_living_standard:</strong><br>\r\n                                            perceived_improvement_in_personal_living_standard<br>\r\n                                            perceived_improvement_in_personal_financial_situation\r\n                                        </div>\r\n                                        <p></p>\r\n                                    </div>\r\n                                    <div class="large-12 medium-12 columns">\r\n                                        <div class="parameter-link">\r\n                                            <a href="#">Available World Sub-subcomponents \r\n                                                <i class="fi-arrow-down"></i><i class="fi-arrow-up"></i>\r\n                                            </a>\r\n                                        </div>\r\n                                        <div class="parameter-list">\r\n                                            <strong>communications_infrastructure:</strong><br>\r\n                                            mobile_phone_subscriptions<br>\r\n                                            broadband_internet_subscriptions<br>\r\n                                            <strong>perceived_current_living_standard:</strong><br>\r\n                                            perceived_personal_living_standard<br>\r\n                                            perceived_national_living_standard<br>\r\n                                            <strong>perceived_improvement_in_living_standard:</strong><br>\r\n                                            perceived_improvement_in_personal_living_standard<br>\r\n                                            perceived_improvement_in_national_living_standard<br>\r\n                                            <strong>religious_engagement:</strong><br>\r\n                                            importance_of_religion<br>\r\n                                            religious_attendance<br>\r\n                                            <strong>prevalence_of_marriage:</strong><br>\r\n                                            marriage_rate<br>\r\n                                            self_reported_marriage<br>\r\n                                            <strong>prevalence_of_divorce:</strong><br>\r\n                                            divorce_rate<br>\r\n                                            self_reported_divorce\r\n                                        </div>\r\n                                        <p></p>\r\n                                    </div>\r\n                                    <p></p>\r\n                                </div>\r\n                            </td>\r\n                        </tr>\r\n                    </tbody>\r\n                </table>\r\n                <p>These Parameters can be combined to increase the specificity of your search. For example:</p>\r\n                <p>http://life-measures-wordpress.connxt.net/api/index.php/us/scores/?key=yourRegistrationKey&amp;locations=va|md|pa&amp;years=2002|2003|2004&amp;dimensions=opportunity|health_and_environment|freedom</p>\r\n                <p>or</p>\r\n                <p>http://life-measures-wordpress.connxt.net/api/index.php/int/scores/?key=yourRegistrationKey&amp;locations=af|al|dz&amp;years=2002|2003|2004&amp;dimensions=opportunity|health_and_environment|freedom</p>\r\n            </article>\r\n        </div>\r\n    </section>\r\n</div>', 'API Documentation', '', 'publish', 'open', 'open', '', 'api-documentation', '', '', '2016-03-09 18:53:51', '2016-03-09 18:53:51', '', 0, 'http://localhost/life-measures-wordpress/?page_id=203', 0, 'page', '', 0),
	(205, 1, '2015-03-05 03:04:40', '2015-03-05 03:04:40', '<section class="intro-image" style="background-image: url(&#39;<?php echo get_template_directory_uri(); ?>/images/cki-template11-headers-api.jpg&#39;);">\r\n    <header>\r\n        <h1>Sign Up to Use Our Data API</h1>\r\n        <hr>\r\n    </header>\r\n</section>\r\n<div id="content" class="row">\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                <h2>API Registration</h2>\r\n                <hr>\r\n            </header>\r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                <form class="api-registration-form" method="post">\r\n                    <div class="row clearfix form-field">\r\n                        <div class="large-6 medium-6 small-12 columns">\r\n                            <label>Your First Name:</label>\r\n                            <input type="text" name="first_name" maxlength="50">\r\n                        </div>\r\n                        <div class="large-6 medium-6 small-12 columns">\r\n                            <label>Your Last Name:</label>\r\n                            <input type="text" name="last_name" maxlength="50">\r\n                        </div>\r\n                    </div>\r\n                    <div class="row clearfix form-field">\r\n                        <div class="large-6 medium-6 small-12 columns">\r\n                            <label>Your Email:</label>\r\n                            <input type="email" name="email" maxlength="50">\r\n                        </div>\r\n                        <div class="large-6 medium-6 small-12 columns">\r\n                            <label>Confirm Email:</label>\r\n                            <input type="email" name="confirm_email" maxlength="50">\r\n                        </div>\r\n                    </div>\r\n                    <div class="row clearfix form-field">\r\n                        <div class="large-12 medium-12 small-12 columns">\r\n                            <label class="checkbox">\r\n                                <input type="checkbox" name="terms" value="1"> I agree to the \r\n                                <a href="<?php echo home_url(); ?>/terms-of-use" target="_blank">Terms &amp; Conditions</a> of using the Quality of Life Measures API.\r\n                            </label>\r\n                        </div>\r\n                    </div>\r\n                    <div class="row clearfix form-field">\r\n                        <div class="large-12 medium-12 small-12 columns">\r\n                            <input class="btn gray flat" type="submit" name="submit" value="Register">\r\n                            <div class="confirmation"></div>\r\n                        </div>\r\n                    </div>\r\n                </form>\r\n        	</article>\r\n        </div>\r\n    </section>\r\n</div>', 'API Registration', '', 'publish', 'open', 'open', '', 'api-registration', '', '', '2016-03-09 18:54:47', '2016-03-09 18:54:47', '', 0, 'http://localhost/life-measures-wordpress/?page_id=205', 0, 'page', '', 0),
	(207, 1, '2015-03-05 03:04:58', '2015-03-05 03:04:58', '<section class="intro-image" style="background-image: url(&#39;<?php echo get_template_directory_uri(); ?>/images/cki-template11-headers-contact.jpg&#39;);">\r\n	<header>\r\n		<h1>CONTACT US</h1>\r\n		<hr>\r\n	</header>\r\n</section>\r\n<div id="content" class="row">\r\n	<section class="post standard">\r\n		<div class="row clearfix">\r\n			<header class="large-3 medium-3 small-12 columns">\r\n				<h2>Contact Us</h2>\r\n				<hr>\r\n			</header>\r\n			<article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n				<p>Your comments and questions about the Quality of Life Measures are welcome. Please use the contact form below and every effort will be made to address responses promptly. </p>\r\n				<form class="contact-form" method="post">\r\n					<div class="row clearfix form-field">\r\n						<div class="large-6 medium-6 small-12 columns">\r\n							<label>Your First Name:</label>\r\n							<input type="text" name="first_name" maxlength="50">\r\n						</div>\r\n						<div class="large-6 medium-6 small-12 columns">\r\n							<label>Your Last Name:</label>\r\n							<input type="text" name="last_name" maxlength="50">\r\n						</div>\r\n					</div>\r\n					<div class="row clearfix form-field">\r\n						<div class="large-6 medium-6 small-12 columns">\r\n							<label>Your Email:</label>\r\n							<input type="email" name="email" maxlength="50">\r\n						</div>\r\n						<div class="large-6 medium-6 small-12 columns">\r\n							<label>Confirm Email:</label>\r\n							<input type="email" name="confirm_email" maxlength="50">\r\n						</div>\r\n					</div>\r\n					<div class="row clearfix form-field">\r\n						<div class="large-6 medium-6 small-12 columns">\r\n							<select name="reason">\r\n								<option value="" selected="">Reason for Contacting Us</option>\r\n								<option value="index">Measurements Question</option>\r\n								<option value="general">General Question</option>\r\n								<option value="media">Media Inquiry</option>\r\n								<option value="other">Other</option>\r\n							</select>\r\n						</div>\r\n						<div class="large-6 medium-6 small-12 columns"></div>\r\n					</div>\r\n					<div class="row clearfix form-field">\r\n						<div class="large-12 medium-12 small-12 columns">\r\n							<label>Your Message:</label>\r\n							<textarea name="message"></textarea>\r\n						</div>\r\n					</div>\r\n					<div class="row clearfix form-field">\r\n						<div class="large-12 medium-12 small-12 columns">\r\n							<label class="checkbox" for="updates">\r\n								<input type="checkbox" name="updates" id="updates" value="1" checked="">\r\n								Please send me occasional update emails about the Quality of Life Measures.\r\n							</label>\r\n						</div>\r\n					</div>\r\n					<div class="row clearfix form-field">\r\n						<div class="large-12 medium-12 small-12 columns">\r\n							<input class="btn gray flat" type="submit" name="" value="Send">\r\n							<div class="confirmation"></div>\r\n						</div>\r\n					</div>\r\n				</form>\r\n			</article>\r\n		</div>\r\n	</section>\r\n</div>', 'Contact us', '', 'publish', 'open', 'open', '', 'contact-us', '', '', '2016-03-09 19:02:07', '2016-03-09 19:02:07', '', 0, 'http://localhost/life-measures-wordpress/?page_id=207', 0, 'page', '', 0),
	(209, 1, '2015-03-05 03:05:07', '2015-03-05 03:05:07', '<section class="intro-image" style="background-image: url(&#39;<?php echo get_template_directory_uri(); ?>/images/cki-template11-headers-faq.jpg&#39;);">\r\n    <header>\r\n        <h1>Frequently Asked Questions</h1>\r\n        <hr>\r\n    </header>\r\n</section>\r\n<div id="content" class="row">\r\n    <a name="initiative"></a>\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                <h2 id="project">Project</h2>\r\n                <hr>\r\n            </header>\r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                <h3>Why explore The Quality of Life Measures?</h3>\r\n                <p>By facilitating an informed discussion about the Quality of Life Measures , assessing its key components, and pursuing research on its key drivers, the hope is to learn more about how to improve quality of life for society at large.</p>\r\n                <p>The concept of the Quality of Life Measures includes both short-term (happiness) and long-term (life satisfaction) components. Our analysis yields five dimensions that are central to quality of life across time, localities, and culture:</p>\r\n                <ol>\r\n                    <li>Opportunity</li>\r\n                    <li>Health &amp; Environment</li>\r\n                    <li>Freedom</li>\r\n                    <li>Community &amp; Relationships</li>\r\n                    <li>Living Standard</li>\r\n                    <li>Peace &amp; Security</li>\r\n                </ol>\r\n                <p>Learn more about the six dimensions <a href="<?php echo home_url(); ?>/constructing-the-measures"><span style="text-decoration: underline;">here</span></a>.</p>\r\n                <h3>How is the Quality of Life Measures relevant to me?</h3>\r\n                <p>Whether you are a scholar, a layman thinker or an involved citizen, the Quality of Life Measures has something valuable to offer you. The goal is to spark an informed and thoughtful dialogue on quality of life and its key drivers. The datasets are publicly available, and we encourage other researchers to use the data to provide feedback and build on the growing body of literature in this important field. For researchers and others alike, the hope is that the data and analysis help to foster a discussion on the drivers of quality of life and, ultimately, improve the lives of individuals and their communities.</p>\r\n            </article>\r\n        </div>\r\n    </section>\r\n    <a name="indices"></a>\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                <h2 id="indices">FAQ</h2>\r\n                <hr>\r\n            </header>\r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                <h3 id="question_1_faqs" class="question">Can you support my research project?</h3>\r\n                <p id="answer_1_faqs" class="answers">More information forthcoming</p>\r\n\r\n                <h3 id="question_2_faqs" class="question">How do you measure quality of life?</h3>\r\n                <p id="answer_2_faqs" class="answers">Researchers have developed several ways of measuring quality of life, from public surveys to monitoring social media, each with its own set of advantages and drawbacks. The Quality of Life Measures looks at nearly fifty different indicators across six dimensions. These indicators describe the environment in which more people are likely to say they are happier and have more life satisfaction. These indicators are turned into scores, which are then aggregated into a composite score for each dimension and for the Quality of Life Measure overall to identify the best (and worst) places to live. These indicators are turned into scores, which are then aggregated into a composite score for each dimension and for quality of life measure overall.</p>\r\n                \r\n                <h3 id="question_3_faqs" class="question">Why six dimensions of Quality of Life Measures?</h3>\r\n                <p id="answer_3_faqs" class="answers">The six dimensions of the Quality of Life Measures arose from a meta-analysis of the empirical literature and a statistical analysis of the World Values Survey.  The analysis identified six groups of variables that were highly correlated with self-reported happiness and life satisfaction. The selection of these groups was bolstered by previous findings in the academic literature.</p>\r\n                \r\n                <h3 id="question_4_faqs" class="question">Where does the data come from?</h3>\r\n                <p id="answer_4_faqs" class="answers">The data in the Measures of Quality of Life comes from third-party sources that are employed in academic work. The data originate primarily from governments or respected non-governmental or international institutions. At the worldwide level, for example, much data comes from the World Bank’s World Governance Indicators, while at the U.S.-level much data comes from the Census.  For a full listing of data sources, see the Sources section of the full <a href="<?php echo home_url(); ?>/get-the-data">report</a>.</p>\r\n                \r\n                <h3 id="question_5_faqs" class="question">Who produces the Measures of Quality of Life?</h3>\r\n                <p id="answer_5_faqs" class="answers">The Measures of Quality of Life are produced by The Baugh Center\'s research team and then reviewed by the Advisory Board and scholars across the world.</p>\r\n                \r\n                <h3 id="question_6_faqs" class="question">What countries does the World Measures of Quality of Life cover?</h3>\r\n                <p id="answer_6_faqs" class="answers">The World Measures of Quality of Life covers every country for which enough data could be obtained to produce a score according to our methodology: 143 countries in all. To learn more, please see the <a href="<?php echo home_url(); ?>/constructing-the-measures/#statistical_analysis">methodology</a> section.</p>\r\n                \r\n                <h3 id="question_7_faqs" class="question">How do the measures take changes over time into consideration?</h3>\r\n                <p id="answer_7_faqs" class="answers">It is possible that the composition of the measures will change, either because of changes in the underlying understanding of Quality of Life or because of changes in data availability. The intention in that case will be to release an entirely new dataset, with changes applied retroactively, to ensure comparability over time.</p>\r\n                \r\n                <h3 id="question_8_faqs" class="question">How often is the data updated?</h3>\r\n                <p id="answer_8_faqs" class="answers">Most of the source data is released no more than once a year. The measures will therefore be updated on an annual basis.</p>\r\n            </article>\r\n        </div>\r\n    </section>\r\n    <a name="components"></a>\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                <h2 id="components">Components</h2>\r\n                <hr>\r\n            </header>\r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                <h3>Components in the Quality of Life Measures</h3>\r\n                <h4>Opportunity</h4>\r\n                <p>Poverty<br>\r\n                Employment<br>\r\n                Community Vitality<br>\r\n                Entrepreneurship</p>\r\n                <h4>Health &amp; Environment</h4>\r\n                <p>Physical Health<br>\r\n                Mental &amp; Emotional Health<br>\r\n                Environmental Health</p>\r\n                <h4>Freedom</h4>\r\n                <p>Civil Liberties<br>\r\n                Religious Freedom<br>\r\n                Political Freedom<br>\r\n                Economic Freedom<br>\r\n                Perception of Freedom</p>\r\n                <h4>Community &amp; Relationships</h4>\r\n                <p>Community Life<br>\r\n                Family Life</p>\r\n                <h4>Living Standard</h4>\r\n                <p>Current Living Standard<br>\r\n                Improvement in Living Standard</p>\r\n                <h4>Peace &amp; Security</h4>\r\n                <p>Violence &amp; Ethnic Warfare<br>\r\n                Human Rights Violations<br>\r\n                Law &amp; Order<br>\r\n                Trust in National Institutions<br>\r\n                Corruption in Business &amp; Government<br>\r\n                Bureaucracy Quality</p>\r\n                <h3>Components in the U.S. Measures of Quality of Life</h3>\r\n                <h4>Opportunity</h4>\r\n                <p>Poverty<br>\r\n                Employment<br>\r\n                Community Vitality<br>\r\n                Education</p>\r\n                <h4>Health &amp; Environment</h4>\r\n                <p>Physical Health<br>\r\n                Mental &amp; Emotional Health<br>\r\n                Environmental Health</p>\r\n                <h4>Freedom</h4>\r\n                <p>Personal Freedom<br>\r\n                Economic Freedom</p>\r\n                <h4>Community &amp; Relationships</h4>\r\n                <p>Community Life<br>\r\n                Family Life</p>\r\n                <h4>Living Standard</h4>\r\n                <p>Current Living Standard<br>\r\n                Improvement in Living Standard</p>\r\n                <h4>Peace &amp; Security</h4>\r\n                <p>Violent Crime Rate<br>\r\n                Property Crime Rate<br>\r\n                Safe Walking at Night<br>\r\n                Trust in Neighbors</p>\r\n            </article>\r\n        </div>\r\n    </section>\r\n</div>', 'FAQS', '', 'publish', 'open', 'open', '', 'faqs', '', '', '2016-03-09 22:45:42', '2016-03-09 22:45:42', '', 0, 'http://localhost/life-measures-wordpress/?page_id=209', 0, 'page', '', 0),
	(211, 1, '2015-03-05 03:05:23', '2015-03-05 03:05:23', '<section class="intro-image" style="background-image: url(&#39;<?php echo get_template_directory_uri(); ?>/images/cki-template11-headers-getdata.jpg&#39;);">\r\n    <header>\r\n        <h1>Download and Interact with the Project’s Data</h1>\r\n        <hr>\r\n    </header>\r\n</section>\r\n<div id="content" class="row">\r\n    <a name="the-data-for-the-indices-of-well-being-are-available-in-four-different-sets-to-suit-different-research-needs"></a>\r\n    <section class="post box post-347 standard_post  large-6 medium-6 small-12 columns">\r\n        <div class="border row clearfix">\r\n            <header>\r\n                <h2>The data for the Quality of Life Measures are available in three different sets to suit different research needs.</h2>\r\n            </header>\r\n            <article>\r\n                <ul>\r\n                    <li>The Standard dataset interpolates missing scores between data points, but it does not back-fill or forward-fill missing values.</li>\r\n                    <li>The Chain-linked dataset uses chain-linking to create a series for overall Quality of Life and each of the dimensions appropriate for time-series analysis.</li>\r\n                    <li>The Raw dataset provides all input data used to create, with no alterations.</li>\r\n                </ul>\r\n                <p>In addition, the data can be downloaded through a REST-ful API, which allows for the creation of custom subsets of the Completed dataset.</p>\r\n            </article>\r\n        </div>\r\n    </section>                 \r\n    <a name="data-sets"></a>\r\n    <section class="post box data_sets large-6 medium-6 columns">\r\n        <div class="border row clearfix">\r\n            <div class="data_set clearfix">\r\n                <header>\r\n                    <h2 id="us_data_sets">United States Datasets</h2>\r\n                </header>\r\n                <article>\r\n                    <div class="data_set_list Standard">\r\n                        <span class="type">Standard</span>\r\n                            <span class="csv download">\r\n                                <a target="_blank" href="<?php echo get_template_directory_uri(); ?>/us_data/StateIndexStandard.csv">CSV</a>\r\n                            </span>\r\n                        <span class="dta download">\r\n                            <a target="_blank" href="<?php echo get_template_directory_uri(); ?>/us_data/StateIndexStandard.dta">Stata</a>\r\n                        </span>\r\n                    </div>\r\n                    <div class="data_set_list Chain-Linked">\r\n                        <span class="type">Chain-Linked</span>\r\n                        <span class="csv download">\r\n                            <a target="_blank" href="<?php echo get_template_directory_uri(); ?>/us_data/StateIndexChained.csv">CSV</a>\r\n                        </span>\r\n                        <span class="dta download">\r\n                            <a target="_blank" href="<?php echo get_template_directory_uri(); ?>/us_data/StateIndexChained.dta">Stata</a>\r\n                        </span>\r\n                    </div>\r\n                    <div class="data_set_list Raw">\r\n                        <span class="type">Raw</span>\r\n                        <span class="csv download">\r\n                        <a target="_blank" href="<?php echo get_template_directory_uri(); ?>/us_data/StateIndexRaw.csv">CSV</a></span>\r\n                            <span class="dta download">\r\n                        <a target="_blank" href="<?php echo get_template_directory_uri(); ?>/us_data/StateIndexRaw.dta">Stata</a></span>\r\n                    </div>\r\n                </article>\r\n            </div>\r\n            <div class="data_set clearfix">\r\n                <header>\r\n                    <h2 id="world_data_sets">World Datasets</h2>\r\n                </header>\r\n                <article>\r\n                    <div class="data_set_list Standard">\r\n                        <span class="type">Standard</span>\r\n                            <span class="csv download">\r\n                                <a target="_blank" href="<?php echo get_template_directory_uri(); ?>/world_data/WorldIndexStandard.csv">CSV</a></span>\r\n                                <span class="dta download">\r\n                                <a target="_blank" href="<?php echo get_template_directory_uri(); ?>/world_data/WorldIndexStandard.dta">Stata</a></span>\r\n                    </div>\r\n                    <div class="data_set_list Chain-Linked">\r\n                        <span class="type">Chain-Linked</span>\r\n                            <span class="csv download">\r\n                                <a target="_blank" href="<?php echo get_template_directory_uri(); ?>/world_data/WorldIndexChained.csv">CSV</a></span>\r\n                                <span class="dta download">\r\n                                <a target="_blank" href="<?php echo get_template_directory_uri(); ?>/world_data/WorldIndexChained.dta">Stata</a></span>\r\n                    </div>\r\n                    <div class="data_set_list Raw">\r\n                        <span class="type">Raw</span>\r\n                        <span class="csv download">\r\n                        <a target="_blank" href="<?php echo get_template_directory_uri(); ?>/world_data/WorldIndexRaw.csv">CSV</a></span>\r\n                        <span class="dta download">\r\n                        <a target="_blank" href="<?php echo get_template_directory_uri(); ?>/world_data/WorldIndexRaw.dta">Stata</a></span>\r\n                    </div>\r\n                </article>\r\n            </div>\r\n        </div>\r\n        <br><br>\r\n    </section>\r\n        <a name="using-the-api"></a>\r\n        <div class="row">\r\n            <section class="post standard">\r\n                <div class="row clearfix">\r\n                    <header class="large-3 medium-3 small-12 columns header-using-the-api">\r\n                        <h2>Using The API</h2>\r\n                        <hr>\r\n                    </header>\r\n                    <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns using-the-api" >\r\n                        <p>The Quality of Life Measures provides a REST-ful API to allow for the downloading of custom selections based on the completed dataset. Using the API, you can download a subset of countries, years, and indicators, as well as recalculate dimension and summary scores from that subset.</p>\r\n                        <p>The API returns data in the JSON format.</p>\r\n                    </article>\r\n                    <article>\r\n                        <a class="btn flat blue btn-data" href="<?php echo home_url(); ?>/api-registration">Register for the API</a>\r\n                    </article>\r\n                </div>\r\n            </section>\r\n            <a name="the-report"></a>\r\n            <section class="post standard">\r\n                <div class="row clearfix">\r\n                    <header class="large-3 medium-3 small-12 columns header-the-report">\r\n                        <h2>The Report</h2>\r\n                        <hr>\r\n                    </header>\r\n                    <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns the-report">\r\n                        <p>The 2014 Measures of Quality of Life presents an innovative and comprehensive picture of Quality of Life Measures throughout the world and across the 50 U.S. States.</p>\r\n                        <p>This inaugural report lays out these new measures, with comprehensive scores and rankings covering 14 years and almost 100 variables between the U.S. states and the world’s countries.</p>\r\n                    </article>\r\n                    <article>\r\n                        <a class="btn flat blue download pdf btn-data" href ="<?php echo get_template_directory_uri(); ?>/pdf/Well-Being_Report_Book.docx" target="_blank" style="background-image: url(&#39;<?php echo get_template_directory_uri(); ?>/images/icons/pdf.png&#39;);">Download the Report</a>\r\n                    </article>\r\n                </div>\r\n            </section>\r\n        </div>\r\n\r\n        <!-- <a name="explore-the-map"></a>\r\n        <section class="post box post-365 standard_post  large-6 medium-6 columns">\r\n            <div class="border row clearfix">\r\n                <div class="content">\r\n                    <img src="<?php echo get_template_directory_uri(); ?>/images/cki-template12-getdata-img.jpg" alt="Explore the Map">\r\n                    <header>\r\n                    </header>\r\n                    <article>\r\n                        <p>The Quality of Life Measures compiled data from all 50 states and 153 countries to determine just how well people are living worldwide.</p>\r\n                        <a class="btn flat blue " href="<?php echo home_url(); ?>/interactive-map">Explore the Map</a>\r\n                    </article>\r\n                </div>\r\n            </div>\r\n        </section> -->\r\n        <!-- <a name="stats"></a>\r\n        <section class="post box stats large-6 medium-6 columns">\r\n            <div class="border row clearfix">\r\n                <ul class="stats-list">\r\n                    <li class="slide dimension border opportunity singapore">\r\n                        <div class="vertical-align">\r\n                            <div class="image-block">\r\n                                <img class=\'svg\' src=\'<?php echo get_template_directory_uri(); ?>/images/svg/SG.svg\' alt=\'Singapore\'>                            \r\n                            </div>\r\n                            <div class="state-name"><span class="border"></span><span class="state">Singapore</span><span class="border"></span></div>\r\n                            <p><p>Singapore ranks 1<sup>st</sup> in Opportunity and Peace &#038; Security</p>\r\n                            </p>\r\n                            <a class="btn flat compare" href="<?php echo home_url(); ?>/interactive-map/int/map/scores/years:2012/locations:SG/dimens:opportunity/">Compare Singapore</a>\r\n                        </div>\r\n                    </li>\r\n                    <li class="slide dimension border opportunity mn">\r\n                        <div class="vertical-align">\r\n                            <div class="image-block">\r\n                                <img class=\'svg\' src=\'<?php echo get_template_directory_uri(); ?>/images/svg/MN.svg\' alt=\'MN\'>                            </div>\r\n                            <div class="state-name"><span class="border"></span><span class="state">MN</span><span class="border"></span></div>\r\n                            <p><p>Minnesota ranks 1<sup>st</sup> in Opportunity</p>\r\n                            </p>\r\n                            <a class="btn flat compare" href="<?php echo home_url(); ?>/interactive-map/us/map/scores/years:2012/locations:MN/dimens:opportunity/">Compare MN</a>\r\n                        </div>\r\n                    </li>\r\n                    <li class="slide dimension border health_and_environment qatar">\r\n                        <div class="vertical-align">\r\n                            <div class="image-block">\r\n                                <img class=\'svg\' src=\'<?php echo get_template_directory_uri(); ?>/images/svg/QA.svg\' alt=\'Qatar\'>                            </div>\r\n                            <div class="state-name"><span class="border"></span><span class="state">Qatar</span><span class="border"></span></div>\r\n                            <p><p>Qatar ranks 1<sup>st</sup> in Health &#038; Environment and Living Standard</p>\r\n                            </p>\r\n                            <a class="btn flat compare" href="<?php echo home_url(); ?>/interactive-map/int/map/scores/years:2012/locations:QA/dimens:health_and_environment/">Compare Qatar</a>\r\n                        </div>\r\n                    </li>\r\n                    <li class="slide dimension border health_and_environment ca">\r\n                        <div class="vertical-align">\r\n                            <div class="image-block">\r\n                                <img class=\'svg\' src=\'<?php echo get_template_directory_uri(); ?>/images/svg/CA.svg\' alt=\'CA\'>                            </div>\r\n                            <div class="state-name"><span class="border"></span><span class="state">CA</span><span class="border"></span></div>\r\n                            <p><p>California ranks 8<sup>th</sup> in Health &#038; Environment</p>\r\n                            </p>\r\n                            <a class="btn flat compare" href="<?php echo home_url(); ?>/interactive-map/us/map/scores/years:2012/locations:CA/dimens:health_and_environment/">Compare CA</a>\r\n                        </div>\r\n                    </li>\r\n                    <li class="slide dimension border freedom new-zealand">\r\n                        <div class="vertical-align">\r\n                            <div class="image-block">\r\n                                <img class=\'svg\' src=\'<?php echo get_template_directory_uri(); ?>/images/svg/NZ.svg\' alt=\'New Zealand\'>                            </div>\r\n                            <div class="state-name"><span class="border"></span><span class="state">New Zealand</span><span class="border"></span></div>\r\n                            <p><p>New Zealand ranks 1<sup>st</sup> for Freedom</p>\r\n                            </p>\r\n                            <a class="btn flat compare" href="<?php echo home_url(); ?>/interactive-map/int/map/scores/years:2012/locations:NZ/dimens:freedom/">Compare New Zealand</a>\r\n                        </div>\r\n                    </li>\r\n                    <li class="slide dimension border freedom ok">\r\n                        <div class="vertical-align">\r\n                            <div class="image-block">\r\n                                <img class=\'svg\' src=\'<?php echo get_template_directory_uri(); ?>/images/svg/OK.svg\' alt=\'OK\'>                            </div>\r\n                            <div class="state-name"><span class="border"></span><span class="state">OK</span><span class="border"></span></div>\r\n                            <p><p>Oklahoma ranks 6<sup>th</sup> in Freedom and 4<sup>th</sup> in Living Standard</p>\r\n                            </p>\r\n                            <a class="btn flat compare" href="<?php echo home_url(); ?>/interactive-map/us/map/scores/years:2012/locations:OK/dimens:freedom/">Compare OK</a>\r\n                        </div>\r\n                    </li>\r\n                    <li class="slide dimension border community_and_relationships sri-lanka">\r\n                        <div class="vertical-align">\r\n                            <div class="image-block">\r\n                                <img class=\'svg\' src=\'<?php echo get_template_directory_uri(); ?>/images/svg/LK.svg\' alt=\'Sri Lanka\'>                            </div>\r\n                            <div class="state-name"><span class="border"></span><span class="state">Sri Lanka</span><span class="border"></span></div>\r\n                            <p><p>Sri Lanka ranks 1<sup>st</sup> in Community &#038; Relationships</p>\r\n                            </p>\r\n                            <a class="btn flat compare" href="<?php echo home_url(); ?>/interactive-map/int/map/scores/years:2012/locations:LK/dimens:community_and_relationships/">Compare Sri Lanka</a>\r\n                        </div>\r\n                    </li>\r\n                    <li class="slide dimension border community_and_relationships ut">\r\n                        <div class="vertical-align">\r\n                            <div class="image-block">\r\n                                <img class=\'svg\' src=\'<?php echo get_template_directory_uri(); ?>/images/svg/UT.svg\' alt=\'UT\'>                            </div>\r\n                            <div class="state-name"><span class="border"></span><span class="state">UT</span><span class="border"></span></div>\r\n                            <p><p>Utah ranks 1<sup>st</sup> in Community &#038; Relationships</p>\r\n                            </p>\r\n                            <a class="btn flat compare" href="<?php echo home_url(); ?>/interactive-map/us/map/scores/years:2012/locations:UT/dimens:community_and_relationships/">Compare UT</a>\r\n                        </div>\r\n                    </li>\r\n                    <li class="slide dimension border living_standard egypt">\r\n                        <div class="vertical-align">\r\n                            <div class="image-block">\r\n                                <img class=\'svg\' src=\'<?php echo get_template_directory_uri(); ?>/images/svg/EG.svg\' alt=\'Egypt\'>                            </div>\r\n                            <div class="state-name"><span class="border"></span><span class="state">Egypt</span><span class="border"></span></div>\r\n                            <p><p>Following the Arab Spring, Egypt saw an increase in Freedom but declines in Living Standard and Peace &#038; Security</p>\r\n                            </p>\r\n                            <a class="btn flat compare" href="<?php echo home_url(); ?>/interactive-map/int/map/scores/years:2012/locations:EG/dimens:living_standard/">Compare Egypt</a>\r\n                        </div>\r\n                    </li>\r\n                    <li class="slide dimension border living_standard mt">\r\n                        <div class="vertical-align">\r\n                            <div class="image-block">\r\n                                <img class=\'svg\' src=\'<?php echo get_template_directory_uri(); ?>/images/svg/MT.svg\' alt=\'MT\'>                            </div>\r\n                            <div class="state-name"><span class="border"></span><span class="state">MT</span><span class="border"></span></div>\r\n                            <p><p>Montana ranks 10<sup>th</sup> in Living Standard</p>\r\n                            </p>\r\n                            <a class="btn flat compare" href="<?php echo home_url(); ?>/interactive-map/us/map/scores/years:2012/locations:MT/dimens:living_standard/">Compare MT</a>\r\n                        </div>\r\n                    </li>\r\n                    <li class="slide dimension border peace_and_security rwanda">\r\n                        <div class="vertical-align">\r\n                            <div class="image-block">\r\n                                <img class=\'svg\' src=\'<?php echo get_template_directory_uri(); ?>/images/svg/RW.svg\' alt=\'Rwanda\'>                            </div>\r\n                            <div class="state-name"><span class="border"></span><span class="state">Rwanda</span><span class="border"></span></div>\r\n                            <p><p>Rwanda improved more on its Peace &#038; Security score from 2000 to 2012 than any other country</p>\r\n                            </p>\r\n                            <a class="btn flat compare" href="<?php echo home_url(); ?>/interactive-map/int/map/scores/years:2012/locations:RW/dimens:peace_and_security/">Compare Rwanda</a>\r\n                        </div>\r\n                    </li>\r\n                    <li class="slide dimension border peace_and_security vt">\r\n                        <div class="vertical-align">\r\n                            <div class="image-block">\r\n                                <img class=\'svg\' src=\'<?php echo get_template_directory_uri(); ?>/images/svg/VT.svg\' alt=\'VT\'>                            </div>\r\n                            <div class="state-name"><span class="border"></span><span class="state">VT</span><span class="border"></span></div>\r\n                            <p><p>Vermont ranks 2<sup>nd</sup> in Peace &#038; Security</p>\r\n                            </p>\r\n                            <a class="btn flat compare" href="<?php echo home_url(); ?>/interactive-map/us/map/scores/years:2012/locations:VT/dimens:peace_and_security/">Compare VT</a>\r\n                        </div>\r\n                    </li>\r\n                </ul>\r\n            </div>\r\n    </section> -->\r\n</div>', 'Get the Data', '', 'publish', 'open', 'open', '', 'get-the-data', '', '', '2016-03-09 19:05:04', '2016-03-09 19:05:04', '', 0, 'http://localhost/life-measures-wordpress/?page_id=211', 0, 'page', '', 0),
