	(216, 1, '2015-03-05 03:06:42', '2015-03-05 03:06:42', '<section class="intro-image" style="background-image: url(&#39;<?php echo get_template_directory_uri(); ?>/images/cki-template11-headers-contact.jpg&#39;);">\r\n    <header>\r\n    	<h1>Media Inquiries</h1>\r\n    	<hr>\r\n    </header>\r\n</section>\r\n<div id="content" class="row">\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                <h2>Media Inquiries</h2>\r\n                <hr>\r\n            </header>\r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                <p> Use the contact form below and every effort will be made to respond to your inquiry in a timely manner.</p>\r\n                <form class="contact-form" method="post">\r\n\r\n                    <div class="row clearfix form-field">\r\n                        <div class="large-6 medium-6 small-12 columns">\r\n                            <label>Your First Name:</label>\r\n                            <input type="text" name="first_name" maxlength="50">\r\n                        </div>\r\n                        <div class="large-6 medium-6 small-12 columns">\r\n                            <label>Your Last Name:</label>\r\n                            <input type="text" name="last_name" maxlength="50">\r\n                        </div>\r\n                    </div>\r\n\r\n                    <div class="row clearfix form-field">\r\n                        <div class="large-6 medium-6 small-12 columns">\r\n                            <label>Your Email:</label>\r\n                            <input type="email" name="email" maxlength="50">\r\n                        </div>\r\n                        <div class="large-6 medium-6 small-12 columns">\r\n                            <label>Confirm Email:</label>\r\n                            <input type="email" name="confirm_email" maxlength="50">\r\n                        </div>\r\n                    </div>\r\n\r\n                    <div class="row clearfix form-field">\r\n                        <div class="large-6 medium-6 small-12 columns">\r\n                            <select name="reason">\r\n                                <option value="" selected="">Reason for Contacting Us</option>\r\n                                <option value="index">Measurements Question</option>\r\n                                <option value="general">General Question</option>\r\n                                <option value="media">Media Inquiry</option>\r\n                                <option value="other">Other</option>\r\n                            </select>\r\n                        </div>\r\n                        <div class="large-6 medium-6 small-12 columns"></div>\r\n                    </div>\r\n\r\n                    <div class="row clearfix form-field">\r\n                        <div class="large-12 medium-12 small-12 columns">\r\n                            <label>Your Message:</label>\r\n                            <textarea name="message"></textarea>\r\n                        </div>\r\n                    </div>\r\n\r\n                    <div class="row clearfix form-field">\r\n                        <div class="large-12 medium-12 small-12 columns">\r\n                            <label class="checkbox" for="updates">\r\n                                <input type="checkbox" name="updates" id="updates" value="1" checked="">\r\n                                Please send me occasional update emails about the Quality of Life Measures.\r\n                            </label>\r\n                        </div>\r\n                    </div>\r\n\r\n                    <div class="row clearfix form-field">\r\n                        <div class="large-12 medium-12 small-12 columns">\r\n                            <input class="btn gray flat" type="submit" name="submit" value="Send">\r\n                            <div class="confirmation"></div>\r\n                        </div>\r\n                    </div>\r\n\r\n                </form>\r\n            </article>\r\n        </div>\r\n    </section>\r\n</div>', 'Media Inquiries', '', 'publish', 'open', 'open', '', 'media-inquiries', '', '', '2016-03-09 19:10:42', '2016-03-09 19:10:42', '', 0, 'http://localhost/life-measures-wordpress/?page_id=216', 0, 'page', '', 0),
	(218, 1, '2015-03-05 03:07:03', '2015-03-05 03:07:03', '<section class="intro-image" style="background-image: url(&#39;<?php echo get_template_directory_uri(); ?>/images/cki-template11-headers-legal.jpg&#39;);">\r\n    <header>\r\n        <h1>New Media Policy</h1>\r\n        <hr>\r\n    </header>\r\n</section>\r\n<div id="content" class="row">\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n            <h2>New Media Policy</h2>\r\n            <hr>\r\n            </header>\r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                <!-- <p>Content Here</p> -->\r\n                <ul>\r\n                    <li>A communication in support of or in opposition to any candidate for public office</li>\r\n                    <li>Abusive, vulgar, offensive, threatening or harassing language, personal attacks of any kind, or offensive terms that target specific individuals or groups</li>\r\n                    <li>Electioneering or lobbying communications within the meaning of applicable federal or state law</li>\r\n                    <li>Off-topic comments,or comments that promote services or products</li>\r\n                    <li>Gratuitous links to sites that could be viewed as spam</li>\r\n                    <li>Personally identifiable information such as social security numbers, addresses, and telephone numbers</li>\r\n                </ul>\r\n                    <p>In addition, the Institute reserves the right, in its sole and absolute discretion, to remove any comments.</p>\r\n                    <p>Please note that the Institute does not necessarily support or endorse any materials posted by third parties.</p>\r\n                    <p>Thank you for your thoughtful contributions to the discussion!</p>\r\n            </article>\r\n        </div>\r\n    </section>\r\n</div>', 'New Media Policy', '', 'publish', 'open', 'open', '', 'new-media-policy', '', '', '2016-03-09 19:11:31', '2016-03-09 19:11:31', '', 0, 'http://localhost/life-measures-wordpress/?page_id=218', 0, 'page', '', 0),
	(220, 1, '2015-03-05 03:07:39', '2015-03-05 03:07:39', '<section class="intro-image" style="background-image: url(&#39;<?php echo get_template_directory_uri(); ?>/images/cki-template11-headers-legal.jpg&#39;);">\r\n    <header>\r\n        <h1>Non-Discriminatory Policy</h1>\r\n        <hr>\r\n    </header>\r\n</section>\r\n<div id="content" class="row">\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                <h2>Non-Discriminatory Policy</h2>\r\n                <hr/>\r\n            </header>\r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                \r\n            </article>\r\n        </div>\r\n    </section>\r\n</div>', 'Non Discriminatory Policy', '', 'publish', 'open', 'open', '', 'non-discriminatory-policy', '', '', '2016-03-09 19:12:17', '2016-03-09 19:12:17', '', 0, 'http://localhost/life-measures-wordpress/?page_id=220', 0, 'page', '', 0),
	(222, 1, '2015-03-05 03:07:58', '2015-03-05 03:07:58', '<section class="intro-image" style="background-image: url(&#39;<?php echo get_template_directory_uri(); ?>/images/cki-template11-headers-legal.jpg&#39;);">\r\n    <header>\r\n        <h1>Privacy Statement</h1>\r\n        <hr>\r\n    </header>\r\n</section>\r\n<div id="content" class="row">\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                <h2>Privacy Statement</h2>\r\n                <hr>\r\n            </header>\r\n        \r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                Privacy Content\r\n            </article>\r\n        </div>\r\n    </section>\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                <h2>Personally Identifiable Information</h2>\r\n                <hr>\r\n            </header>\r\n        <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n            <p>There are certain actions and areas of the University Site that require you to provide personal information, which may include: (a) requesting educational materials; (b) joining the University Site or creating a user account; (c) submitting a comment; and (d) applying for an University educational program.</p>\r\n            <p>The personal information we collect may consist or include, but is not limited to, requesting your name, address, phone number, mobile phone number, email address, username, and birth date.  By submitting personal information to us, you agree that we may use the information in accordance with this privacy policy. We will update this privacy policy as we deem necessary. You should be sure to reread this policy in its entirety from time to time.</p>\r\n            <p>If you do submit information to us, we will use that information for the purposes for which it was submitted.  For instance, if you join the University Site and ask to be placed on our email list, we will use your email address to send you alerts and other messages about topics and developments we think may be of interest to you. Likewise, for any web-based educational projects we may collect and use personally identifiable information in connection with such educational efforts. In order for us to convey educational information to you, we may require a user to provide a name, address, phone number, mobile phone number, email address, username, and birth date. Information collected may be used for the promotion of our educational activities.</p>\r\n            <p>We may also use your information in other ways, such as to contact you about other activities or as otherwise disclosed to you at the point of collection, and we may share or transfer your information with our affiliates and any other organizations or entities for similar purposes.  Additionally, to the extent required by applicable federal or state law, we will provide this information to others.  We may share personally and non-personally identifiable information with third parties that help manage the University Site and databases, as well as with our affiliates or other third parties.</p>\r\n            <p>For example:</p>\r\n            <ul>\r\n                <li>If you post a comment on the University Site, we will post the screen name under which the submission was made.  Certainly, any information you include in a posting will be viewable by any visitor to the University Site.</li>\r\n                <li>If in connection with certain aspects of the University Site you provide us with your name, address, phone number, mobile phone number, email address, or username, we may use this information to contact you about issues that we believe may be of interest to you.  We may also share or transfer this information to other organizations.</li>\r\n            </ul>\r\n            <p>By submitting this information to us, you give us permission to receive it, review it, edit it, store it, post it on the University Site, and use it in appropriate settings designed to advance the educational mission of University. To protect your privacy, we strongly urge you to not include any identifying information in your submissions.</p>\r\n            <p>You should be aware that we use third-party vendors to provide services on the University Site and in our offline business operations. The information that you submit on the University Site may be provided to those vendors on a confidential basis so that those vendors can provide services (such as maintaining our databases, sending email messages to our visitors, or otherwise facilitating our educational activities) on our behalf.</p>\r\n            <p>We also reserve the right to share personal information you provide with legal authorities if it is, in our sole opinion, necessary to investigate, prevent, or otherwise take action regarding suspected fraud, situations involving threats or potential harm, or if otherwise required by law (such as by a subpoena, court order, or other legal means). In the event that we reasonably believe that you have violated this Privacy Policy or the <a title="Terms of Use" href="<?php echo home_url(); ?>/terms-of-us" target="_blank">Terms of Use</a> governing your use of the University Site, we reserve the right to use any information we have collected about you in connection with any efforts we make or made by any of our agents on our behalf.</p>\r\n            <h3>User Comments</h3>\r\n            <p>Please be aware that any personal information you provide in connection with comments you contribute to the University Site or any other interactive features we may offer will be visible to other users and visitors to those websites.</p>\r\n            <h3>Polls, Surveys, Sweepstakes, Giveaways, Contests, and Promotions</h3>\r\n            <p>University may request information from you via online polls, surveys, sweepstakes, giveaways, or contests. Participation in polls, surveys, sweepstakes, giveaways, or contests is voluntary; you have a choice whether or not to disclose information required to enter. Information requested may include personal contact information such as your name, address, phone number, mobile phone number, email address, username, birth date, and/or similar information. We may share this information with our affiliates, vendors, and other organizations or entities. Contact information will be used to notify winners and award prizes. Contact information may also be used to contact you with further information about University, as well as the information about third parties whose activities relate to the educational mission of University.  Survey information, and related information you provide to University, may be used for the purposes of monitoring or improving use of and satisfaction with the University Site.</p>\r\n            <h3>Online Advertisements</h3>\r\n            <p>University may run online advertisements on third-party websites to attract visitors to the University Site or to alert the public to opportunities related to our educational mission.  Our advertising vendors may use cookies and clear GIFs on the University Site and on the third-party sites on which our ads appear, which allow the advertising vendor to track certain information about user behavior, such as “click-stream data.”  Any use of information by an advertising vendor is governed by their terms of use and privacy policy, so you should familiarize yourself with these policies prior to responding to online advertisements.</p>\r\n            <h3>Text Messages and Other Points of Contact</h3>\r\n            <p>If you sign up to receive text messages from us, University may collect, store, and use your mobile phone number to send you such messages.  We may also collect and store other personally identifiable information, such as your name, address, phone number, mobile phone number, email address, username, and/or similar information you may opt to provide to us.  If you provide us with your email address, we may use it to send you email newsletters and updates from University.  In addition, as noted above, we may share this information with our affiliates, vendors, and other organizations or entities.</p>\r\n            <h3>Email Newsletters and Updates</h3>\r\n            <p>If you sign up to receive email newsletters and updates from University and its affiliates and/or organizations that aid or share University’s educational mission, we may collect, store, and use your email address to send you information from University. We may also collect and store other personally identifiable information you give University, including but not limited to your name, address, phone number, mobile phone number, email address, username, birth date, and/or similar information you may opt to provide us. If you provide us with your mobile phone number, University may use it to send you text messages. In addition, as noted above, we may share this information with our affiliates, vendors, and other organizations or entities to advance University’s educational mission.</p>\r\n            <h3>Employment Opportunities</h3>\r\n            <p>You may choose to apply for employment, an internship position, or to participate in an educational program with University through online application forms we may provide, or by sending us, by email or otherwise, a resume and letter (containing personally identifiable information). Any information you provide to us in this regard may be viewed by University and any third parties who assist University in recruiting, selection, and similar services for the purpose of evaluating your application and/or responding to your employment-related inquiry. University may share any or all of the materials you submit related to job opportunities with affiliates and other organizations in furtherance of University’s educational mission.</p>\r\n            <h3>Other Communications</h3>\r\n            <p>You may decide to contact University, by email or otherwise, with other inquiries or to provide feedback. Any personally identifiable information you provide in connection with such inquiries or feedback may be collected and used by University for the purpose of processing and responding to your inquiries or feedback.</p>\r\n        </article>\r\n        </div>\r\n    </section>\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                <h2>Non-Personally Identifiable Information</h2>\r\n                <hr>\r\n            </header>\r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                <p>University gathers certain broad information about the use of the University Site, including the number of unique visitors, the frequency with which they visit, and the programs and services preferred. We look at the data in summary form, rather than on an individual basis. We gather this information so we can learn how many people visit the University Site, see which pages are the most and least viewed, identify which websites are referring visitors to the University Site, and related information. The data helps us determine the extent to which visitors use certain parts of the University Site. Thus, we use this information to help us in restructuring the University Site to better meet the needs of our users.</p>\r\n                <p>In connection with these activities we may use some of the below mentioned tools.</p>\r\n                <h3>Cookies</h3>\r\n                <p>We use cookies to track usage and to enhance the efficiency of user interaction with the University Site, gain insight into and customize your use of the University Site, and help maintain the University Site’s proper operation. Cookies are small files placed on the hard drive of your computer; each website can send its own cookie to your browser if your browser preferences allow it. You can configure your browser to accept all cookies, reject all cookies, or notify you when a cookie is set. (Each browser is different, so check the “Help” menu of your browser to learn how to change your cookie preferences.) You are always free to decline our cookies if your browser permits, although that may prevent you from using certain features on the University Site. Please note that several companies offer utilities designed to help you visit websites anonymously.</p>\r\n                <p>Generally, we will not allow third parties to place any cookies, web beacons, or other similar devices on the University Site. University may allow third parties to place cookies on the University Site, such as the below-referenced Google Analytics, in order to improve services University provides to advance our educational mission. We do not control devices that may be placed on the University Site by third parties. University is not responsible for any other websites accessible from or linked to this website, including their privacy policies or information collection practices.</p>\r\n                <h3>Google Analytics</h3>\r\n                <p>University uses Google Analytics, a service that uses cookies and tracking code to analyze how visitors use the University Site. The information collected (including your browser and operating system, IP address, pages visited on our website, and time spent here) is transmitted to and stored by Google. Google uses this information to evaluate visitors’ use of the University Site in an aggregate manner. Google compiles reports on website activity and provides other related services. Google may transfer this information to third parties that process information on its behalf or as required by law. Please take the time to familiarize yourself with Google’s privacy policy.</p>\r\n                <h3>IP Addresses</h3>\r\n                <p>We collect and analyze traffic on the University Site by keeping track of the IP addresses of our visitors. An IP address is the unique numeric identifier assigned to each computer browser accessing the internet. An IP address, by itself, cannot identify you personally.  When combined with other information provided by your Internet Service Provider (“ISP”), however, your IP address can be used to identify the computer originating a message.</p>\r\n            </article>\r\n        </div>\r\n    </section>\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                <h2>Modifications to this Privacy Policy</h2>\r\n                <hr>\r\n            </header>\r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                <p>We may amend or change this Privacy Policy at any time. In the event of any material change, University will post a clear notice of such a revision to this Policy on the University Site or email you such notice at an email address you have provided to University.</p>\r\n            </article>\r\n        </div>\r\n    </section>\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                <h2>How to Update Your Information, Unsubscribe, or Contact Us</h2>\r\n                <hr>\r\n            </header>\r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                <p>We wish to maintain your information accurately, and to allow you to make choices about how we use it.  If you wish to review the contact information we have on file for you, correct your contact information, provide a change of address, or inform us how you wish your contact information to be used, please contact our staff. You may contact us by email at: <!-- <a href="mailto:email@charleskochUniversity.org">email@charleskochUniversity.org</a></p> -->\r\n                <p>If you do not want to receive any email from us in the future, click the “unsubscribe” reference in any email we send you. Otherwise, you may email us at<strong> (Email here) </strong><!-- <a href="mailto:email@charleskochUniversity.org">email@charleskochUniversity.org</a> --> with the subject line reading “UNSUBSCRIBE” to complete the opt-out process, or send a letter to the below address requesting that we unsubscribe you from our contact lists. Note that absent a specific request your records will remain in our database, but your email address and other information used to contact you will be deactivated.</p>\r\n            </article>\r\n        </div>\r\n    </section>\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                <h2>How We Collect Information</h2>\r\n                <hr>\r\n            </header>\r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                <p>The University collects both personally identifiable information and non-personally identifiable information from you in a number of ways. Personally identifiable information is generally information that identifies you, such as your name, telephone number, postal address, email address, and birth date. Non-personally identifiable information is information that does not personally identify you, including anonymous information and aggregate data. The University uses this information to understand better how visitors use the University Site, research our visitors’ demographics, interests, and behavior, provide visitors with customized services and information, improve our promotional and outreach efforts, and for other similar purposes.</p>\r\n            </article>\r\n        </div>\r\n    </section>\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                <h2>Complaints or Questions</h2>\r\n                <hr>\r\n            </header>\r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                \r\n                \r\n            </article>\r\n        </div>\r\n    </section>\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                <h2>A Special Note about Children</h2>\r\n                <hr>\r\n            </header>\r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                <p>We encourage everyone to visit the University Site and learn about the University. Children, however, are not eligible to register to use the services of the University, and we ask that minors not submit any personal information to us. If you are a minor, you may use this service only in conjunction with a parent or guardian. Parents or guardians should become knowledgeable about software and other tools so they can assure that children’s use of the Internet reflects parental preferences and requirements.</p>\r\n            </article>\r\n        </div>\r\n    </section>\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                <h2>Security</h2>\r\n                <hr>\r\n            </header>\r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                <p>Although the University Site has certain security measures in place to protect the loss, misuse, and alteration of the information you provide, no email or web transmission is completely secure against interception or incorrect routing. Thus, there is always the risk that unauthorized persons may access or use your personally identifiable information.  If you are worried because your communication is very sensitive, do not send it electronically. Please consider this prior to submitting any information to us via the University Site. You use the University Site and send us such information at your own risk.</p>\r\n                <p>Although we take reasonable measure to protect your privacy on the University Site, we need your help.  You have control over your own privacy. Think carefully before providing any personal or financial information online. If you desire to remain anonymous when you write to us or send us submissions you should not tell us your name, the names of other users, or of your friends. To fully protect your privacy, you should try to exclude any information that you think may identify you or others.</p>\r\n                <p>Remember that we cannot protect your privacy once you leave the University Site, such as by hyperlink. We do not control the privacy policies of other websites. So when you visit other websites, check each site’s privacy policy, and be careful about the information you submit or share. In addition, you should only forward our email messages or other website information (such as our job postings) to others through the buttons or links provided within the email or the University Site. When you use your email system’s forwarding capabilities, others may be able to take actions in your name and/or access your personal information.</p>\r\n            </article>\r\n        </div>\r\n    </section>\r\n</div>', 'Privacy Statement', '', 'publish', 'open', 'open', '', 'privacy-statement', '', '', '2016-03-09 19:13:00', '2016-03-09 19:13:00', '', 0, 'http://localhost/life-measures-wordpress/?page_id=222', 0, 'page', '', 0),
	(224, 1, '2015-03-05 03:08:13', '2015-03-05 03:08:13', '<section class="intro-image" style="background-image: url(&#39;<?php echo get_template_directory_uri(); ?>/images/cki-template11-headers-indicies.jpg&#39;);">\r\n        <header>\r\n            <h1>Constructing the Quality of Life Measures</h1>\r\n            <hr>\r\n        </header>\r\n    </section>\r\n    <div id="content">\r\n        <div class="row">\r\n            <a name="constructing-the-indices"></a>\r\n            <section class="post standard">\r\n                <div class="row clearfix">\r\n                    <header class="large-3 medium-3 small-12 columns">\r\n                    <h2 id="contstructing_the_indices">Constructing the Measures</h2>\r\n                    <hr>\r\n                    </header>\r\n                    <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                        <p>Drawing on a long history of literature, beginning with ancient Greek philosophical works and ending with modern economic and psychological empirical studies, the Quality of Life Initiative defines wellbeing or quality of life as consisting of two parts: happiness, understood as the range of positive feelings stretching from contentment to joy that a person experiences at any given time, and life satisfaction, understood as a cognitive evaluation of one’s life as a whole. Additionally, our research has found that personal wellbeing derives largely from two broad sources: livability, the external factors that inhibit or enable Quality of Life, and life ability, the internal capabilities that allow individuals to enjoy and master their environment.</p>\r\n                        <p><strong>Issues of Data-availability across countries does that the Quality of Life Measures capture livability slightly better than they capture lifeability.</strong></p>\r\n                    </article>\r\n                </div>\r\n            </section>\r\n            <a name="statistical-analysis"></a>\r\n            <section class="post standard">\r\n                <div class="row clearfix">\r\n                    <header class="large-3 medium-3 small-12 columns">\r\n                        <h2 id="statistical_analysis">Statistical Analysis</h2>\r\n                        <hr>\r\n                    </header>\r\n                    <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                        <p>The literature review and empirical meta-analysis identified an initial set of recurring variables found to impact happiness and life satisfaction (see the Initiative’s literature reviews here). A Principal Component Analysis of approximately one hundred variables in the World Values Survey was used to identify the underlying attributes of these factors. The complete set of factors identified through this analysis was incorporated in an Ordinary Least Squares regression and A Structural Equation Model controlling for other factors. The analysis revealed a set of variables with a significant positive impact on Quality of Life. These findings were subjected to several forms of sensitivity analysis. The significant factors and controls were grouped together in six dimensions according to their weights and theoretical considerations to form the six dimensions of Quality of Life.</p>\r\n                    </article>\r\n                </div>\r\n            </section>\r\n            <a name="creating-and-validating-the-dimensions"></a>\r\n            <section class="post standard">\r\n                <div class="row clearfix">\r\n                    <header class="large-3 medium-3 small-12 columns">\r\n                        <h2 id="creating_and_validation_the_dimensions">Creating and Validating the Dimensions</h2>\r\n                        <hr>\r\n                    </header>\r\n                    <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                        <p>The six dimensions WERE RECONSTRUCTED using a combination of self-reported and objective indicators to correct for the disadvantages of each. The specific indicators were chosen based on statistical analysis, previous findings in the literature, comparability across time and geography, availability, and source reputation in order to ensure the highest possible level of quality. Because previous research has found that outcomes relative to expectations are strong predictors of Quality of Life, changes in some variables were selected in addition to actual levels.</p>\r\n                        <p>The indicators selected were tested analytically through Confirmatory Factor Analysis, Average Variance Extracted, and the Cronbach’s Alpha method.</p>\r\n                        <p>Indicators were normalized using an approach modeled on the Human Development Index from the United Nations. Goalposts were chosen to represent the maximum and minimum values for each indicator, either from the source or from an analysis of the data. Individual scores were calculated using a linearization between the two goalposts. Once the scores for indicators were obtained, they were aggregated hierarchically, with each score equaling either the score of the associated indicator or the average of the scores one level below it. The score for Dimension 1 is therefore the average of the score for Components 1A and 1B while the score for Component 1A is the average of the score for Subcomponents 1Ai and 1Aii.</p>\r\n                        <p>Additional research and analysis relative to the project can be found <a href="<?php echo home_url(); ?>/learn-about-the-research">here</a>.</p>\r\n                    </article>\r\n                </div>\r\n            </section>\r\n        </div>\r\n        <a name="dimensions"></a>\r\n        <section class="carousel dimensions constructing-the-measures">\r\n        <div class="row">\r\n            <header class="title-group">\r\n                <h2 id="six_dimensions">Five Foundations of Quality of Life</h2>\r\n                <hr/>\r\n                <p>\r\n                    The Quality of Life Measures compiled data from a whole host of sources to compose this United States measurements. Various sources include (but are not limited to) the World Bank, Freedom House, Center for Systemic Peace, U.S. Census, the Bureau of Labor Statistics, Gallup, and the National Center for Educational Statistics.\r\n                </p>\r\n            </header>\r\n            <div class="slide-toggle clearfix">\r\n                <a href="#" class="us selected">U.S. Measurements</a><a href="#" class="world">World Measurements</a>\r\n            </div>\r\n            <ul class="slide-list">\r\n                <li class="slide us">\r\n                	<div class="dimension large-4-half medium-6 small-12 columns-no-padding community_and_relationships">\r\n                        <div class="title">\r\n                            <div class="image-container">\r\n                                <img id="symbol-rounded" src="<?php echo get_template_directory_uri(); ?>/images/dimensions_symbol/Symbol-CommunityRelationships.png">\r\n                            </div>\r\n                            <h3>Community &#038; Relationships</h3>\r\n                        </div>\r\n                         <div class="dimension-components">\r\n                            <h4>Civic Commitment</h4>\r\n	                     	<h4>Religious Engagement</h4>\r\n	                    	<h4>Family Life</h4>\r\n	                        <h4>Social life</h4>\r\n                        </div>\r\n                    </div>\r\n                    <div class="dimension large-4-half medium-6 small-12 columns-no-padding opportunity">\r\n                        <div class="title">\r\n                            <div class="image-container">\r\n                                <img id="symbol-rounded" src="<?php echo get_template_directory_uri(); ?>/images/dimensions_symbol/Symbol_Opportunity.png">\r\n                            </div>\r\n                            <h3>Freedom &#038; <br>Opportunity</h3>\r\n                        </div>\r\n                        <div class="dimension-components">\r\n                            <h4>Personal Freedom</h4>\r\n	                        <h4>Economic Freedom</h4>\r\n	                        <h4>Employment</h4>\r\n	                        <h4>Perceptions of Opportunities</h4>\r\n                        </div>\r\n                    </div>\r\n                    <div class="dimension large-4-half medium-6 small-12 columns-no-padding health_and_environment">\r\n                        <div class="title">\r\n                            <div class="image-container">\r\n                                <img id="symbol-rounded" src="<?php echo get_template_directory_uri(); ?>/images/dimensions_symbol/Symbol-HealthEnvironment.png">\r\n                            </div>\r\n                            <h3>Health &#038; Environment</h3>\r\n                        </div>\r\n                         <div class="dimension-components">\r\n                            <h4>Physical Health</h4>\r\n                            <h4>Mental Health</h4>\r\n                            <h4>Environmental Health</h4>\r\n                        </div>\r\n                    </div>\r\n                    <!-- <div class="dimension large-4 medium-6 small-12 columns-no-padding freedom">\r\n                        <div class="title">\r\n                            <div class="image-container">\r\n                                <img id="symbol-rounded" src="<?php echo get_template_directory_uri(); ?>/images/dimensions_symbol/Symbol-Freedom.png">\r\n                            </div>\r\n                            <h3>Freedom</h3>\r\n                        </div>\r\n                         <div class="dimension-components">\r\n                            <h4>Personal Freedom</h4>\r\n                            <h4>Economic Freedom</h4>\r\n                        </div>\r\n                    </div> -->\r\n                    <div class="dimension large-4-half medium-6 small-12 columns-no-padding living_standard">\r\n                        <div class="title">\r\n                            <div class="image-container">\r\n                                <img id="symbol-rounded" src="<?php echo get_template_directory_uri(); ?>/images/dimensions_symbol/Symbol-LivingStandard.png">\r\n                            </div>\r\n                            <h3>Living Standard</h3>\r\n                        </div>\r\n                         <div class="dimension-components">\r\n                            <h4>Income</h4>\r\n	                        <h4>Poverty</h4>\r\n	                        <h4>Perceptions</h4>\r\n                        </div>\r\n                    </div>\r\n                    <div class="dimension large-4-half medium-6 small-12 columns-no-padding peace_and_security">\r\n                        <div class="title">\r\n                            <div class="image-container">\r\n                                <img id="symbol-rounded" src="<?php echo get_template_directory_uri(); ?>/images/dimensions_symbol/Symbol-PeaceSecurity.png">\r\n                            </div>\r\n                            <h3>Peace &#038; Security</h3>\r\n                        </div>\r\n                         <div class="dimension-components">\r\n                            <h4>Violent Crime</h4>\r\n	                        <h4>Theft</h4>\r\n	                        <h4>Trust</h4>\r\n	                        <h4>Safety Perceptions</h4>\r\n                        </div>\r\n                    </div>\r\n                </li>\r\n                <li class="slide world">\r\n                	<div class="dimension large-4-half medium-6 small-12 columns-no-padding community_and_relationships">\r\n                        <div class="title">\r\n                            <div class="image-container">\r\n                                <img id="symbol-rounded" src="<?php echo get_template_directory_uri(); ?>/images/dimensions_symbol/Symbol-CommunityRelationships.png">\r\n                            </div>\r\n                            <h3>Community &#038; Relationships</h3>\r\n                        </div>\r\n                         <div class="dimension-components">\r\n                            <h4>Civic Commitment</h4>\r\n	                        <h4>Religious Engagement</h4>\r\n	                        <h4>Family Life</h4>\r\n	                        <h4>Social Life</h4>\r\n                        </div>\r\n                    </div>\r\n                    <div class="dimension large-4-half medium-6 small-12 columns-no-padding opportunity">\r\n                        <div class="title">\r\n                            <div class="image-container">\r\n                                <img id="symbol-rounded" src="<?php echo get_template_directory_uri(); ?>/images/dimensions_symbol/Symbol_Opportunity.png">\r\n                            </div>\r\n                            <h3>Freedom &#038; <br>Opportunity</h3>\r\n                        </div>\r\n                         <div class="dimension-components">\r\n                            <h4>Political Freedom</h4>\r\n	                        <h4>Civil Liberties</h4>\r\n	                        <h4>Religious Freedom</h4>\r\n	                        <h4>Economic Freedom</h4>\r\n	                        <h4>Perceived Opportunities</h4>\r\n	                        <h4>Entrepreneurship</h4>\r\n	                        <h4>Employment</h4>\r\n                        </div>\r\n                    </div>\r\n                    <div class="dimension large-4-half medium-6 small-12 columns-no-padding health_and_environment">\r\n                        <div class="title">\r\n                            <div class="image-container">\r\n                                <img id="symbol-rounded" src="<?php echo get_template_directory_uri(); ?>/images/dimensions_symbol/Symbol-HealthEnvironment.png">\r\n                            </div>\r\n                            <h3>Health &#038; Environment</h3>\r\n                        </div>\r\n                         <div class="dimension-components">\r\n                            <h4>Physical Health</h4>\r\n                            <h4>Mental Health</h4>\r\n                            <h4>Environmental Health</h4>\r\n                        </div>\r\n                    </div>\r\n                    <!-- <div class="dimension large-4 medium-6 small-12 columns-no-padding freedom">\r\n                        <div class="title">\r\n                            <div class="image-container">\r\n                                <img id="symbol-rounded" src="<?php echo get_template_directory_uri(); ?>/images/dimensions_symbol/Symbol-Freedom.png">\r\n                            </div>\r\n                            <h3>Freedom</h3>\r\n                        </div>\r\n                         <div class="dimension-components">\r\n                            <h4>Political Freedom</h4>\r\n                            <h4>Civil Liberties</h4>\r\n                            <h4>Religious Freedom</h4>\r\n                            <h4>Economic Freedom</h4>\r\n                            <h4>Perceived Freedom</h4>\r\n                        </div>\r\n                    </div> -->\r\n                    <div class="dimension large-4-half medium-6 small-12 columns-no-padding living_standard">\r\n                        <div class="title">\r\n                            <div class="image-container">\r\n                                <img id="symbol-rounded" src="<?php echo get_template_directory_uri(); ?>/images/dimensions_symbol/Symbol-LivingStandard.png">\r\n                            </div>\r\n                            <h3>Living Standard</h3>\r\n                        </div>\r\n                          <div class="dimension-components">\r\n                            <h4>Income</h4>\r\n	                        <h4>Consumption</h4>\r\n	                        <h4>Poverty</h4>\r\n	                        <h4>Access to Resources</h4>\r\n                        </div>\r\n                    </div>\r\n                    <div class="dimension large-4-half medium-6 small-12 columns-no-padding peace_and_security">\r\n                        <div class="title">\r\n                            <div class="image-container">\r\n                                <img id="symbol-rounded" src="<?php echo get_template_directory_uri(); ?>/images/dimensions_symbol/Symbol-PeaceSecurity.png">\r\n                            </div>\r\n                            <h3>Peace &#038; Security</h3>\r\n                        </div>\r\n                        <div class="dimension-components peace_and_security">\r\n                            <h4>Violence and Warfare</h4>\r\n	                        <h4>Human Rights</h4>\r\n	                        <h4>Safety Perceptions</h4>\r\n	                        <h4>Trust in Institutions</h4>\r\n	                        <h4>Corruption</h4>\r\n	                        <h4>Bureaucratic Quality</h4>\r\n                        </div>\r\n                    </div>\r\n                </li>\r\n            </ul>\r\n            <p>Learn more about the sources of these measurements.</p>\r\n            <a class="btn flat gray download pdf variables" href="<?php echo get_template_directory_uri(); ?>/pdf/Quality_of_Life_Variables_US_Measures.pdf" target="_blank" style="background-image: url(&#39;<?php echo get_template_directory_uri(); ?>/images/icons/pdf.png&#39;);">Download U.S. Variables</a>\r\n        </div>\r\n    </section>\r\n    <div class="row">\r\n        <a name="glossary"></a>\r\n        <section class="glossary">\r\n            <header class="title-group">\r\n            <h2>Glossary</h2>\r\n            <hr>\r\n            </header>\r\n            <div class="row">\r\n                <div class="glossary-item-container large-12 medium-12 small-12 columns">\r\n                    <div class="glossary-item">\r\n                        <h3>Component</h3>\r\n                        <p>The different qualities of a particular dimension. For example, the components of the freedom dimension are civil liberties, economic freedom, political freedom, religious freedom, and perception of freedom.</p>\r\n                    </div>\r\n                    <div class="glossary-item">\r\n                        <h3>Component Score</h3>\r\n                        <p>The value obtained from averaging the scores for subcomponents within a particular component for a country or U.S. state.</p>\r\n                    </div>\r\n                    <div class="glossary-item">\r\n                        <h3>Dimension</h3>\r\n                        <p>One of the six key elements of Quality of Life Measures(i.e., Living Standard, Health &amp; Environment, Freedom, Opportunity, Community &amp; Relationships, or Peace &amp; Security) as defined by the Measures of Quality of Life.</p>\r\n                    </div>\r\n                    <div class="glossary-item">\r\n                        <h3>Dimension Score</h3>\r\n                        <p>The value obtained from averaging the scores for components within a particular dimension for a country or U.S. state.</p>\r\n                    </div>\r\n                    <div class="glossary-item">\r\n                        <h3>Happiness</h3>\r\n                        <p>The range of positive feelings, stretching from contentment to joy, that a person experiences.</p>\r\n                    </div>\r\n                    <div class="glossary-item">\r\n                        <h3>Life Satisfaction</h3>\r\n                        <p>A person’s cognitive, evaluative reflection of that individual’s life as a whole.</p>\r\n                    </div>\r\n                    <div class="glossary-item">\r\n                        <h3>Life ability</h3>\r\n                        <p>A measurement of an individual’s internal capacity to be happy and satisfied and his or her adeptness at taking advantage of the external environment.</p>\r\n                    </div>\r\n                    <div class="glossary-item">\r\n                        <h3>Livability</h3>\r\n                        <p>A measurement of how the external, societal environment enables Quality of Life Measures.</p>\r\n                    </div>\r\n                    <div class="glossary-item">\r\n                        <h3>Normalization</h3>\r\n                        <p>The method used to reconcile variables that are based on different units of measurement.</p>\r\n                    </div>\r\n                    <div class="glossary-item">\r\n                        <h3>Objective Variables</h3>\r\n                        <p>Quantifiable social or economic variables that are based on specific standards and reported by outside observers.</p>\r\n                    </div>\r\n                    <div class="glossary-item">\r\n                        <h3>Score</h3>\r\n                        <p>The normalized value used to estimate each variable. The variable scores are aggregated for subcomponents, components, and dimensions and result in the final measurement of the Quality of Life of a country or U.S. state.</p>\r\n                    </div>\r\n                    <div class="glossary-item">\r\n                        <h3>Subcomponent</h3>\r\n                        <p>One of two or more factors of a component. For example, the Living Standard dimension consists of the two components of current living standard and improvement in living standard. The improvement in living standard component includes the 5-year growth in GDP and perceptions of improvement in living standard as subcomponents.</p>\r\n                    </div>\r\n                    <div class="glossary-item">\r\n                        <h3>Subcomponent Score</h3>\r\n                        <p>The value obtained from averaging the scores for variables within a particular subcomponent for a country or U.S. state.</p>\r\n                    </div>\r\n                    <div class="glossary-item">\r\n                        <h3>Subjective Variables</h3>\r\n                        <p>Variables that are individually perceived and obtained through self-reported methods.</p>\r\n                    </div>\r\n                    <div class="glossary-item">\r\n                        <h3>Variable</h3>\r\n                        <p>One of many attributes used to characterize a certain aspect of the Quality of Life Measures of a country or U.S. state. There are 50 variables in the World measurements and 42 in the U.S. measurements.</p>\r\n                    </div>\r\n                    <div class="glossary-item">\r\n                        <h3>Variable Score</h3>\r\n                        <p>The normalized value used to estimate each attribute of the Quality of Life Measures of a country or U.S. state.</p>\r\n                    </div>\r\n                    <div class="glossary-item">\r\n                        <h3>Quality of Life Measures Score</h3>\r\n                        <p>The value obtained from averaging the six dimension scores for a country or U.S. state.<br>\r\n                        The Quality of Life Measures consists of the two underlying concepts of happiness and life satisfaction and emanates both from societal factors (livability) and from the internal personal dynamics that enable the individual to benefit from the societal environment (life ability).</p>\r\n                    </div>\r\n                </div>\r\n            </div>\r\n        </section>\r\n    </div>\r\n</div>', 'Constructing the Measures', '', 'publish', 'open', 'open', '', 'constructing-the-measures', '', '', '2016-03-10 18:52:31', '2016-03-10 18:52:31', '', 0, 'http://localhost/life-measures-wordpress/?page_id=224', 0, 'page', '', 0),
	(226, 1, '2015-03-05 03:08:25', '2015-03-05 03:08:25', '<section class="intro-image" style="background-image: url(&#39;<?php echo get_template_directory_uri(); ?>/images/cki-template11-headers-research.jpg&#39;);">\r\n	<header>\r\n	    <h1>Learn About Our Research</h1>\r\n	    <hr>\r\n	</header>\r\n</section>\r\n<div id="content" class="row">\r\n	<a name="research"></a>\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                <h2>Research</h2>\r\n                <hr>\r\n            </header>\r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                <p>Enhancing the understanding of Quality of Life and its origins requires a journey of inquiry and open debate. Below, find research and analysis that we hope will advance the comprehension of human wellbeing and quality of life. The ideas presented here are meant to stimulate new research questions, foster a robust dialogue, and spark discussions about how to improve individual and societal quality of life.</p>\r\n            </article>\r\n        </div>\r\n    </section>\r\n    <a name="the-indices-of-well-being"></a>\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                <h2>The Measures of Quality of Life</h2>\r\n                <hr>\r\n            </header>\r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                <h3>Download the full report</h3>\r\n                <ul>\r\n                    <li><a href="<?php echo get_template_directory_uri(); ?>/pdf/Well-Being_Report_Book.docx" target="_blank">The Measures of Quality of Life</a></li>\r\n                </ul>\r\n                <h3>Or individual sections</h3>\r\n                <ul>\r\n                    <li><a href="<?php echo get_template_directory_uri(); ?>/pdf/Well-Being_Report_Book.docx" target="_blank">Executive Summary</a></li>\r\n                    <li><a href="<?php echo get_template_directory_uri(); ?>/pdf/Well-Being_Report_Book.docx" target="_blank">Chapter 1: Quality of Life Measures: An Introduction</a></li>\r\n                    <li><a href="<?php echo get_template_directory_uri(); ?>/pdf/Well-Being_Report_Book.docx" target="_blank">Chapter 2: Rankings and Trends</a></li>\r\n                    <li><a href="<?php echo get_template_directory_uri(); ?>/pdf/Well-Being_Report_Book.docx" target="_blank">Chapter 3: Methodology</a></li>\r\n                    <li><a href="<?php echo get_template_directory_uri(); ?>/pdf/Well-Being_Report_Book.docx" target="_blank">Chapter 4: Country and State Results</a></li>\r\n                    <li><a href="<?php echo get_template_directory_uri(); ?>/pdf/Well-Being_Report_Book.docx" target="_blank">Issues in Constructing Reliable Composite Measures</a></li>\r\n                    <li><a href="<?php echo get_template_directory_uri(); ?>/pdf/Well-Being_Report_Book.docx" target="_blank">References</a></li>\r\n                </ul>\r\n            </article>\r\n        </div>\r\n    </section>\r\n    <a name="additional-research"></a>\r\n    <section class="post standard">\r\n        <div class="row clearfix">\r\n            <header class="large-3 medium-3 small-12 columns">\r\n                <h2>Additional Research</h2>\r\n                <hr>\r\n            </header>\r\n            <article class="large-8 large-offset-1 medium-8 medium-offset-1 small-12 columns">\r\n                <ul>\r\n                    <li>Human Quality of Life Measures: An Introduction and Brief Historical Background</li>\r\n                    <li>Issues in Constructing Reliable Composite Measurements: Methodological Challenges for Complex Measures</li>\r\n                    <li><a href="<?php echo get_template_directory_uri(); ?>/pdf/Meta_analysis_Quality_of_Life_Eger_Maridal.pdf" target="_blank">A Meta-Analysis of the Quality of Life Literature</a></li>\r\n                    <li>Cultural Dynamics of Subjective Quality of Life Measures</li>\r\n                </ul>\r\n            </article>\r\n        </div>\r\n    </section>\r\n</div>', 'Learn About The Research', '', 'publish', 'open', 'closed', '', 'learn-about-the-research', '', '', '2016-03-09 19:09:51', '2016-03-09 19:09:51', '', 0, 'http://localhost/life-measures-wordpress/?page_id=226', 0, 'page', '', 0),
