INSERT INTO `lm_wpss_questions` (`id`, `question`, `type`, `quiz_id`) VALUES
	(1, 'In this very moment, how happy do you feel?', 'radio', 1),
	(2, 'What age group do you belong to?', 'radio', 1),
	(3, 'Are you? ', 'radio', 1),
	(4, 'Are you currently?', 'radio', 1),
	(5, 'Are you currently?', 'radio', 1),
	(6, 'What economic class do you consider yourself as belonging to?', 'radio', 1),
	(7, 'How satisfied are you with your job?', 'radio', 1),
	(8, 'How satisfied are you with your family life <span style=\\\'color:#666\\\'>(relationship with your parents, your spouse, your children and less on the more distant relatives)</span>?', 'radio', 1),
	(9, 'How many close friends/family members do you have whom you trust and rely on?', 'radio', 1),
	(10, 'How good is your health these days?', 'radio', 1),
	(11, 'How often do you attend a house of worship or religious service?', 'radio', 1),
	(12, 'How often do you participate in charitable activities (i.e., volunteering or helping others for free)?', 'radio', 1),
	(13, 'How often do you participate in sports and social activities (anything that involves doing something you enjoy with other people in your spare time)?', 'radio', 1),
	(14, 'How much freedom and control do you feel you currently have over the way your life is turning out?\r\n<br><br>\r\n<small class=\'scale1\'>1 <i class="fi-arrow-right"></i> No Freedom</small>\r\n<small class=\'scale10\'>10 <i class="fi-arrow-right"></i> Full Freedom</small>', 'radio', 1),
	(15, 'How do you feel about your commute to and from work/school?', 'radio', 1),
	(16, 'How safe do you feel walking at night where you live?', 'radio', 1),
	(17, 'How trustworthy do you feel the people in your neighborhood are?', 'radio', 1),
	(18, 'How much purpose and meaning do you feel your life has?', 'radio', 1),
	(19, 'How would people <span style=\\\'color:#666\\\'>(i.e., family, friends, and colleagues)</span> describe your attitude?', 'radio', 1),
	(20, 'How hopeful are you of achieving the things you desire most in life?', 'radio', 1),
	(21, '<span id=\\\'hover_title\\\' value=\\"This question is trying to get to virtue and  discipline. It aims to have you answer:\r\nDo you feel you make decisions for your and other’s long-term benefit or mostly for immediate pleasure?\r\nAnd,  Do you make decisions based on your conscience or on impulse)\\">Do you make choices in your life according to your principles (what you believe to be right) or do you tend to focus on instant gratification?</span>\r\n\r\n\r\n<br><br>\r\n<small class=\\\'scale1\\\'>1 <i class="fi-arrow-right"></i> I have no self-control</small>\r\n<small class=\\\'scale10\\\'>10 <i class="fi-arrow-right"></i> I have great self-control</small>', 'radio', 1),
	(22, 'Do you spend time pondering, appreciating, and being grateful for the positives, both large and small, in your life?', 'radio', 1),
	(23, 'Are you able to choose to think about positive and productive things or do you often find yourself unfocused with thoughts spiraling into negativity? \r\n\r\n<br><br><small class=\\\'scale1\\\'>1 <i class="fi-arrow-right"></i> Unfocused, Negative Thoughts </small> \r\n<small class=\\\'scale10\\\'> 10 <i class="fi-arrow-right"></i> Productive, Positive Thoughts </small>', 'radio', 1),
	(24, 'How frequently do you interact with animals? ', 'radio', 1),
	(25, 'Considering the way your life has gone in recent months and years and your prospects for the future, how satisfied are you with the way your life is turning out?\r\n\r\n<br><br><small class=\\\'scale1\\\'>1 <i class="fi-arrow-right"></i> Completely Unsatisfied</small>\r\n<small class=\\\'scale10\\\'>10 <i class="fi-arrow-right"></i> Very Satisfied</small>', 'radio', 1);
INSERT INTO `lm_wpss_quizzes` (`id`, `quiz_title`, `submit_txt`, `submit_button_txt`, `store_results`, `send_admin_email`, `admin_email_addr`, `record_submit_info`, `record_fields`, `send_user_email`, `user_email_content`, `user_email_from_name`, `user_email_from_address`, `quiz_questions`, `score_routes`, `integrate_wplogin`) VALUES
	(1, 'Well Being Quiz', '', 'Click to Submit', 1, 0, 'support@wdoitsolutions.com', 0, '', 0, '<p>Thank you for taking our [quiztitle]</p><p>You scored [score] and were routed to:<br />[routed]</p><p>Summary:</p><p>[answers]</p>', 'Well Being Quiz', 'support@wdoitsolutions.com', '', '', 1);
